#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
float hd = 1.;//height information
float hash(vec2 p)
{
    p  = 50.0*fract( p*0.3183099);
    return fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                              hash( i + vec2(1.0,0.0) ), u.x),
                         mix( hash( i + vec2(0.0,1.0) ), 
                              hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

float fbm( vec2 p )
{
    return 0.5000*texture( iChannel0, p*1.00 ).x;//+ 
           //0.2500*texture( iChannel0, p*2.02 ).x+
           //0.250*texture( iChannel0, p*4.05 ).x;

}
float bgm( vec2 p )
{
    return 0.5000*texture( iChannel1, p*1.00 ).x+ 
           0.2500*texture( iChannel1, p*2.02 ).x+
           0.250*texture( iChannel1, p*4.05 ).x;

}
mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}


float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float GetDist(vec3 p,inout float hd) {
    float bgm = bgm(vec2(0.1,1));//bgm anima
    bgm = clamp(0.5,1.,bgm);

    float w1 = noise(p.xz+iTime);
    float w2 = noise(p.xz-iTime*1.);
    w1 = w1+ w2;
    //w1 = pow(w1,2.);

    float w3 = fbm(p.xz*.01 + iTime*.01)*0.3;
    float w4 = fbm(p.xz*.02 - iTime*.04)*0.2;


    float w = w1*.6+ w3+w4;//wave
    //w = w1*.6;
    //w = w3 + w4;

    float sinOffset =  sin(p.x*1.+ iTime)*.05 + sin(p.z*4. + iTime*2.)*.05;
    sinOffset = sinOffset * .3;
    float hd1 = sinOffset + w*0.2;//wave height
    hd = hd1 + bgm*.1;

    vec3 pn = vec3(0,1,0);
    pn.x = p.x*0.01 + sin(iTime)*.01;//wobble
    pn.z = p.z*0.01 + cos(iTime)*.01;

    pn.xz = pn.xz*(sin(iTime*1.)*.26 + .89);//convex
    //pn.xz = pn.xz*(sin(iTime*1.)*.5+.7);//wave concave ,convex 
    float plane = dot(p,normalize(pn)) +2. + hd1;//plane distance

    vec3 bp = p-vec3(0,sin(iTime)*.5+ .5,0);

    bp = abs(bp);
    bp.xz -= 1.;


    float scale = mix(1.,5.,smoothstep(-1.,1.,bp.y));

    bp.xz *= scale;
    bp.xz *= Rot(bp.y+iTime);



    bp.xz = abs(bp.xz);
    bp.xz -= 1.5;



    float box = sdBox(bp, vec3(1,1,1));

    //box = sdBox(p-vec3(0,1,0)-sin(p.x* 6.5+iTime*4.)*.02, vec3(1,.02,1));//wave
    //box = abs(box)-.1;   //making  shell
    vec4 s1 = vec4(0,1.5+ bgm,0,1.);
    float sd1 = length(p-s1.xyz) - s1.w-bgm;
    float sd = sd1;
    float d = smin(plane, box*.1,0.9);
    d= smin(sd, d,0.7);

    //sky
    float sdsky = length(p)- 52.;
    sdsky = abs(sdsky) - 1.5;

    d = min(d,sdsky);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float h = 1.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p,hd);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {

	float d = GetDist(p, hd);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy, hd),
        GetDist(p-e.yxy, hd),
        GetDist(p-e.yyx, hd));

    return normalize(n);
}

vec3 GetLight(vec3 p,vec3 rd, vec3 lightPos, vec3 lightCol) {
    vec3 v = -rd;


    //attenuation
    float atten = 1. - length(lightPos - p)/20.;//float is light Area
    atten = smoothstep(1.,.5,clamp(0.,1.,atten));

    float MoveRadius = 0.;
    float MoveSpeed = 0.;

    //float t = iTime * MoveSpeed;
    //lightPos.xz += vec2(sin(t), cos(t)) * MoveRadius;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    vec3 ref = reflect(n,rd);
    //ambient
    float amb = sqrt(clamp( 0.5+0.5*n.y, 0.0, 1.0 ));
    //diffuse
    float NdotL = dot(n, l);
    NdotL = NdotL*.5 + .5;
    float dif = clamp(NdotL, 0. , 1.);
    //specular
    vec3 h = normalize(l + v);
    float spe = pow(max(dot(n,h),0.), 128.);
    //fresnel
    //float fs = .4; //fresnel scale





    float fre =  clamp(  1.+ dot(rd,n)       ,0.,1.);
    fre = pow(fre, 4.);
    //dom
    float dom = smoothstep( -0.2, 0.2, ref.y );


    //ficker
    vec4 fcol = texture(iChannel3,p.xz);


    vec3 finCol = vec3(0.); 
    finCol += amb * vec3(1,1,0) * .5;
    finCol += dif * vec3(1,1,1) * .5;
    finCol += spe * vec3(1,1,1) * 1.;
    finCol *= fre * vec3(1,1,0)* .1;
   	finCol += dom * vec3(0,1,1) * 0.1;
    vec3 refT = texture(iChannel2,ref).xyz;
    finCol += refT*.5*( dot(rd,n)*.5+.5);


    finCol *= atten*lightCol*1.4;
    finCol *= 2.;
    //finCol = vec3(dom);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    //if(p.y<.01 && d<length(lightPos-p)) dif *= 0.3;//shadow

    //color adjust
    finCol.rg *= 0.5;
    finCol.g *= .7;
    //finCol = i
    return finCol ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
