    for(int i = 0; i < 4; ++i){
        if(rayMarch(r0, rd, d))
        {
            if(matId == 1.0)
            {
                albedo = red;
            }
            else if(matId == 2.0)
            {
                albedo = green;
            }
            else if(matId == 3.0)
            {
                if(dot(norm(r0 + d * rd), vec3(0.0, 1.0, 0.0)) < 0.0)
