vec3 bgColor(const ray r){
    float theta = atan(r.dir.z, r.dir.x);
    float phi = asin(r.dir.y/length(r.dir));

    vec2 p = vec2(theta/2., phi);

    float octaves = 8.;
    float H = 1.;
    float f = 0.5 + 0.5 * snoise(p);

    f = fractalSum(p, 2., H, octaves);

    float marble = 0.5 * sin(50. * p.y + 145.*f + iTime * 0.5) + 0.5;

    return mix(vec3(0.9), mix(vec3(0.3, 0.5, 1.0), vec3(0.0, 0.3, 1.0), (phi+M_PI/2.)/M_PI), marble);
}

hit hitSphere(const ray r, const sphere s){
    vec3 o2o = r.ori - s.ori;
    float a = dot(r.dir, r.dir);
    float b = 2.0 * dot(o2o, r.dir);
    float c = dot(o2o, o2o) - s.r*s.r;
    float delta = b*b - 4.0*a*c;
    if(delta<0.0){
            // no solution, return dummy
        return  dummyHit;
    }
    else {
        float sqrtDelta = sqrt(delta);
        float t;
        float temp = (-b-sqrtDelta)/(2.*a);
        if(temp > minT){t = temp;}
        else {t = (-b+sqrtDelta)/(2.*a);}
        if(t < minT)return dummyHit;
        vec3 hitP = r.ori + t*r.dir;
        return hit(t, hitP, (hitP-s.ori)/s.r, s.mat);
    }
}


hit hitCylinder(const ray r,cylinder cld){
    vec3 o2o = r.ori - cld.ori;
    float a = dot(r.dir.yz, r.dir.yz);
    float b = 2.0 * dot(o2o.yz, r.dir.yz);
    float c = dot(o2o.yz, o2o.yz) - cld.r*cld.r;
    float delta = b*b - 4.0*a*c;
    if(delta<0.0){
        return  dummyHit;
    }
    else {
        float sqrtDelta = sqrt(delta);
        float t;
        float temp = (-b-sqrtDelta)/(2.*a);
        if(temp > 0.01){t = temp;}
        else {t = (-b+sqrtDelta)/(2.*a);}
        if(t < 0.01)return dummyHit;
        vec3 hitP = r.ori + t*r.dir;
        return hit(t, hitP, normalize(vec3(0, (hitP-cld.ori).yz)), cld.mat);
    }
}

hit hitCap(const ray r, const cap cp, int cId){
    vec3 o2o = r.ori - cp.ori;
    float a = dot(r.dir, r.dir);
    float b = 2.0 * dot(o2o, r.dir);
    float c = dot(o2o, o2o) - cp.r2*cp.r2;
    float delta = b*b - 4.0*a*c;
    if(delta<0.0){
            // no solution, return dummy
        return  dummyHit;
    }
    else {
        float sqrtDelta = sqrt(delta);
        float t1 = (-b-sqrtDelta)/(2.*a);
        float t2 = (-b+sqrtDelta)/(2.*a);

        if(t2 < minT){
            return dummyHit;
        }

        float dirCos = dot(cp.norm,r.dir)/ length(r.dir);
        float sOri2Plane = sqrt(cp.r2*cp.r2 - cp.r1*cp.r1);
        float rOri2Plane = dot(r.ori - cp.ori, cp.norm) - sOri2Plane;

        hit hit1 = dummyHit;
        if(rOri2Plane < 0. && dirCos > 0. || rOri2Plane > 0. && dirCos < 0.){
            float t =  rOri2Plane/dirCos /length(r.dir);
            t = abs(t);
            vec3 hitP = r.ori + t* r.dir;
            vec3 center = cp.ori + cp.norm * sOri2Plane;
            vec2 uv = (hitP.xy - center.xy + vec2(cp.r1))/cp.r1/2.*vec2(iResolution.y);
            if(t > minT && length(hitP - (cp.ori + sOri2Plane * cp.norm)) < cp.r1){
                hit1 = hit(t, hitP, -cp.norm, cp.mat);
                vec4 col = texture (iChannel3, getUvForCap(iResolution.xy, uv, cId));
                if(col.r * col.a > 0.2){
                    if(cId == 0){
                        hit1.mat = material(LAMBERTIAN,col.a * vec3(1.0,0.7,0.0), 0.);
                    }else if(cId == 1){
                        hit1.mat = material(LAMBERTIAN,col.a * vec3(0.1,0.3,0.8), 0.);
                    }
                    else if(cId == 2){
                        hit1.mat = material(LAMBERTIAN,col.a * vec3(0.9,0.3,0.3), 0.);
                    }
                }
            }
        }

        if(t1 > minT){
            vec3 hitP = r.ori + t1*r.dir;
            float dist2Plane = dot(hitP - cp.ori, cp.norm) - sOri2Plane;
            if(dist2Plane > 0.){
                if(hit1.t > -1. && hit1.t < t1)
                    return hit1;
                else 
                    return hit(t1, hitP, (hitP-cp.ori)/cp.r2, cp.mat);
            }       
            if(hit1.t > -1.)
                return hit1;
        }

        vec3 hitP = r.ori + t2*r.dir;
        float dist2Plane = dot(hitP - cp.ori, cp.norm) - sOri2Plane;
        if(dist2Plane > 0.){
            if(hit1.t > -1. && hit1.t < t2)
                return hit1;
            else 
                return hit(t2, hitP, (hitP-cp.ori)/cp.r2, cp.mat);
        }
        if(hit1.t > -1.)
            return hit1; 
        return dummyHit;
    }
}

vec3 color(ray r, cap[BALLNUM] cp, cylinder cld){
    vec3 col = vec3(1);
    int depth = 8;
    while(depth != 0){
            hit h = dummyHit;
        for(int i = 0; i < cp.length(); i++){
                hit tempH = hitCap(r, cp[i], i);
            if(tempH.t > 0. && (h.t < 0. || h.t > tempH.t)){
                    float newSeed = g_seed * float(depth);
                h = tempH;
                //if(h.t > minT && dot(h.normal, r.dir) > 0.)col *= vec3(1.0, 0, 0);
            }
        }

        hit tempH = hitCylinder(r, cld);
        if(tempH.t > 0. && (h.t < 0. || h.t > tempH.t)){
            float newSeed = g_seed * float(depth);
            h = tempH;
        }

        if(h.t > 0.){
               vec3 scatter = r.dir;
           float newSeed = g_seed * float(depth); // This would give the result a constant look
            //float newSeed = g_seed * iTime; // This would give the result a changing look
           if(h.mat.type == LAMBERTIAN){
                   scatter = normalize(h.normal) + normalize(random_in_unit_sphere(newSeed));
               col *= h.mat.albedo;
            }
            else if(h.mat.type == METAL){
                   vec3 reflectDir = reflect(normalize(r.dir), normalize(h.normal));
               reflectDir =normalize(reflectDir)+ h.mat.roughness * normalize(random_in_unit_sphere(newSeed));
               if(dot(reflectDir, h.normal) < 0.){
                       col = vec3(0);
                   break;
                }
               else{
                   col *= h.mat.albedo;
                   scatter = reflectDir;
               }
            }
            else if(h.mat.type == DIELECTRIC){
                    vec3 outNorm;
                float ni2nt;  
                float cosine;
                if(dot(r.dir, h.normal) > 0.){
                        //col = vec3(1000,0,0);
                    outNorm = -h.normal;
                    ni2nt = h.mat.roughness;
                    cosine = ni2nt * dot(normalize(r.dir), normalize(h.normal));

                }
                else{
                        outNorm = h.normal;
                    ni2nt = 1.0/h.mat.roughness;
                    cosine = -dot(normalize(r.dir), normalize(h.normal));
                }
                vec3 reflectDir = reflect(normalize(r.dir), normalize(h.normal));
                vec3 refractDir = refract(normalize(r.dir), normalize(outNorm),ni2nt);
                float reflectProb = 0.;               
                if(length(refractDir) > 0.0001){
                        reflectProb = schlick(cosine, ni2nt);
                }
                else{
                        reflectProb = 1.;
                }
                if(rand1(newSeed) > reflectProb){
                        //if(dot(refractDir, h.normal)<0.)col*=vec3(1, 0, 0);
                    scatter = refractDir;
                }
                else{
                        scatter = reflectDir;
                }
            }
            r = ray(h.p, scatter);
            depth--;
        }
        else {
                col *= bgColor(r);
            break;
        }
    }
    return col;
}

