// a Better camera structure
struct iCamera{
    vec3 lookFrom;
    vec3 lookAt;
    vec3 up;
    float fov;
    float aperture;
};

struct camera{
    vec3 origin;
    vec3 horizontal;
    vec3 vertical;
    vec3 LowerLeftCorner;
    float focusDist;
    float aperture;
};

camera getCamera(iCamera ic, vec2 iResolution){
    vec3 lookDir = ic.lookFrom-ic.lookAt;
    float focusDist = length(lookDir);
    lookDir = normalize(lookDir);
    vec3 horizontal = normalize(cross(ic.up, lookDir));
    vec3 vertical = normalize(cross(lookDir, horizontal));
    float halfHeight = tan(ic.fov*M_PI/360.);
    float halfWidth = halfHeight * iResolution.x / iResolution.y;
    vec3 lowerLeftCorner = ic.lookFrom - halfWidth*horizontal*focusDist - halfHeight*vertical*focusDist - lookDir*focusDist;
    horizontal *= 2. * halfWidth;
    vertical *= 2. * halfHeight;
    camera c = camera(ic.lookFrom, horizontal, vertical, lowerLeftCorner, focusDist, ic.aperture);
    return c;
}

