float fade(float t){
    return 1. - smoothstep(0.0, 1.0, abs(t));
}



vec2 grad(vec2 cell){
    float angle = radians(360. * hash12(cell));
    return vec2(cos(angle), sin(angle));
}

float bump(vec2 tt, vec2 p){
    vec2 g = grad(p);
    return  fade(tt.x) * fade(tt.y) * dot(g, tt);
}

// return a random value between -1 and 1
float snoise(vec2 p){
    vec2 cell = floor(p);
    vec2 frac = p - cell;
    float res = 0.;
    res += bump(frac, cell);
    res += bump(vec2(frac.x-1.,frac.y), cell+vec2(1, 0));
    res += bump(vec2(frac.x, frac.y-1.), cell+vec2(0, 1));
    res += bump(vec2(frac.x-1.,frac.y-1.), cell + vec2(1));
    return res;
}


float fractalSum(vec2 p, float domainScale, float H, float octaves){
    float result = 0.;
    float valueScale = 1.;
    float valueScaleInc = pow(domainScale, -H);
    for(float octave = octaves; octave > 0.; --octave, p*=domainScale, valueScale*=valueScaleInc){
        float n = snoise(p);
        float fade = min(1., octave);
        result += valueScale * n * fade;
    }
    result /= (1.0- pow(valueScaleInc, octaves))/(1.0 - valueScaleInc);
    return 0.5 + 0.5 * result;
}

