#define BUF_SIZE iChannelResolution[2].xy

const float txRow = 16.;
const float txSize = txRow * txRow / 2.;

const vec2 G = vec2(0., -16000.*9.8f); // external (gravitational) forces
const float REST_DENS = 1000.f; // rest density
const float GAS_CONST = 2000.f; // const for equation of state
const float H = 26.f; // kernel radius
const float HSQ = H*H; // radius^2 for optimization
const float MASS = 65.f; // assume all particles have the same mass
const float VISC = 250.f; // viscosity constant
const float DT = 0.0008f; // integration timestep

// smoothing kernels defined in Mller and their gradients
const float POLY6 = 315.f/(65.f*M_PI*pow(H, 9.f));
const float SPIKY_GRAD = -45.f/(M_PI*pow(H, 6.f));
const float VISC_LAP = 45.f/(M_PI*pow(H, 6.f));

// simulation parameters
const float EPS = H/2.; // boundary epsilon
const float BOUND_DAMPING = -0.5f;

float r;
vec2 center;

struct Particle {
    vec2 x;
    vec2 v;
    vec2 f;
    float p; // pressure
    float rho; // rho (density)
};

vec2 getUvForCap(vec2 iResolution, vec2 uv, int cId){
    float stepSize  = iResolution.x / 3.;
    return vec2(stepSize * float(cId) + uv.x/3., uv.y)/iResolution.xy;
}
