void ComputeDensityPressure(int id, int cId)
{
    p.rho = 0.f;
    for(int j = 0; j < int(txSize); j++)
    {
        vec4 jXV = LoadParticle(j, cId);
        if(jXV.x < .0)continue;
        vec2 rij = jXV.xy - p.x;
        float r2 = dot(rij, rij);
        if(r2 < HSQ)
        {
            // this computation is symmetric
            p.rho += MASS*POLY6*pow(HSQ-r2, 3.f);
        }
    }
    p.p = GAS_CONST*(p.rho - REST_DENS);
}

