void ComputeForces(int mId, int cId)
{
    vec2 fpress = vec2(0);
    vec2 fvisc = vec2(0);
    //vec2 iOther = LoadOther(mId);

    for(int j = 0; j < int(txSize); j++)
    {
        if(mId == j)
            continue;

        vec4 jXV = LoadParticle(j, cId);
        if(jXV.x < .0)continue;
        vec2 rij = jXV.xy - p.x;
        float r = length(rij);

        if(r < H)
        {      
            vec2 jOther = LoadOther(j, cId).ba; // Get rho and p of particle j

            // compute pressure force contribution
            fpress += -normalize(rij)*MASS*(p.p + jOther.y)/(2.f *jOther.x) * SPIKY_GRAD*pow(H-r,2.);
            // compute viscosity force contribution
            fvisc += VISC*MASS*(jXV.gb - p.v)/jOther.x * VISC_LAP*(H-r);
        }
    }
    vec2 fgrav = G * p.rho;
    p.f = fpress + fvisc + fgrav;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int mId;
    vec2 kv = floor(fragCoord);
    mId = int (kv.x + txRow * kv.y);
