    int cId = mId/int(txSize);
    mId = mId - cId * int(txSize);
    vec4 col;
    if(iFrame < 5){
        col = Init(mId, cId);
    }
    else {
        p.x = LoadParticle(mId, cId).xy;
        p.v = LoadParticle(mId, cId).ba;
        //ComputeDensityPressure(mId);
        p.rho = LoadOther(mId, cId).b;
        p.p = LoadOther(mId, cId).a;
        ComputeForces(mId, cId);
        col = vec4(p.f, p.rho, p.p);
    }
    fragColor = col;
}

