hit hitCap(const ray r, const cap cp){
    vec3 o2o = r.ori - cp.ori;
    float a = dot(r.dir, r.dir);
    float b = 2.0 * dot(o2o, r.dir);
    float c = dot(o2o, o2o) - cp.r2*cp.r2;
    float delta = b*b - 4.0*a*c;
    if(delta<0.0){
        // no solution, return dummy
        return  dummyHit;
    }
    else {
        float sqrtDelta = sqrt(delta);
        float t1 = (-b-sqrtDelta)/(2.*a);
        float t2 = (-b+sqrtDelta)/(2.*a);

        if(t2 < minT){
            return dummyHit;
        }

        float dirCos = dot(cp.norm,r.dir)/ length(r.dir);
        float sOri2Plane = sqrt(cp.r2*cp.r2 - cp.r1*cp.r1);
        float rOri2Plane = dot(r.ori - cp.ori, cp.norm) - sOri2Plane;

        hit hit1 = dummyHit;
        if(rOri2Plane < 0. && dirCos > 0. || rOri2Plane > 0. && dirCos < 0.){
            float t =  rOri2Plane/dirCos /length(r.dir);
            t = abs(t);
            vec3 hitP = r.ori + t* r.dir;
            if(t > minT && length(hitP - (cp.ori + sOri2Plane * cp.norm)) < cp.r1){
                hit1 = hit(t, hitP, -cp.norm, cp.mat);
            }
        }

        if(t1 > minT){
            vec3 hitP = r.ori + t1*r.dir;
            float dist2Plane = dot(hitP - cp.ori, cp.norm) - sOri2Plane;
            if(dist2Plane > 0.){
                if(hit1.t > -1. && hit1.t < t1)
                    return hit1;
                else 
                    return hit(t1, hitP, (hitP-cp.ori)/cp.r2, cp.mat);
            }       
            if(hit1.t > -1.)
                return hit1;
        }

        vec3 hitP = r.ori + t2*r.dir;
        float dist2Plane = dot(hitP - cp.ori, cp.norm) - sOri2Plane;
        if(dist2Plane > 0.){
            if(hit1.t > -1. && hit1.t < t2)
                return hit1;
            else 
                return hit(t2, hitP, (hitP-cp.ori)/cp.r2, cp.mat);
        }
        if(hit1.t > -1.)
            return hit1; 
        return dummyHit;
    }
}

vec4 InitSPH(int mId, int cId)
{
    vec2 ptcStep = vec2(r) / txRow * 2.;
    float x = ptcStep.x * mod(float(mId), txRow);
    float y = ptcStep.y * floor(float(mId)/txRow);
    init_rand(vec2(x, y), 2.); 
    x += (rand1(g_seed)-0.5) * ptcStep.x/2.;
    if(dot(vec2(x, y) - center, vec2(x, y) - center) < r*r)
    {
        return vec4(x, y, 0, 0);
    }
    else return vec4(-1);
}

void Integrate(int mId, int cId)
{
    // forward Euler integration
    vec4 other = LoadOther(mId, cId);
    //p.v += DT*p.f/p.rho;
    p.v += DT*other.xy/other.z + vec2(12000. * (-LoadCap(cId).a), 0.);
    p.x += DT*p.v;
    //p.x += other.x;
    //p.x += 0.1;

    //float offSet = LoadCap(cId).a;
    //p.x.x -= 200. * offSet;
    vec2 toCenter = p.x - center;

    if(dot(toCenter, toCenter) > (r - EPS)*(r - EPS)){
        p.v = reflect(normalize(p.v), normalize(-toCenter)) * length(p.v)/2.;
        p.x = center + normalize(toCenter) * (r - EPS);
    }

    else{
        vec4 col = texture (iChannel3, getUvForCap(iResolution.xy, p.x, cId));
        if(col.a > 1.){
            p.x -= DT*p.v; 
            p.v =  (p.v+ rand2(g_seed) * length(p.v)/5.) * BOUND_DAMPING ;
            //p.v *= BOUND_DAMPING;
        }
        // Return to the original position, v inverse
        // Not the right way to calculate bounce
    }

    //p.x.x += offSet;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int mId;
    vec2 kv = floor(fragCoord);
    mId = int (kv.x + txRow * kv.y);
    if (kv.x >= txRow) {
        discard;
    }
    else if(float(mId) < 3. * txSize){
        r = min(iResolution.x, iResolution.y)/2.;
        center = vec2(r);
        vec4 col;
        int cId = mId / int(txSize);
        mId = mId - cId * int(txSize);
        if(iFrame < 5){
            fragColor = InitSPH(mId, cId);
        }
        else {
            p.x = LoadParticle(mId, cId).xy;
            p.v = LoadParticle(mId, cId).ba;
            Integrate(mId, cId);
            fragColor = vec4(p.x, p.v);
        }
    }
    else if(float(mId) < 3. * txSize + 3.){
        // Recording position of caps   
        int index = mId - 3 * int(txSize);
        fragColor = LoadCap(index); 

        if(index == 0){
            if(fragColor.r == 0.){
                fragColor = vec4(-1,0, 1,0);
            }
        }
        else if(index == 1){
            if(fragColor.r == 0.){
                fragColor = vec4(0, 0, 1,0);
            }
        }
        else if(index == 2){
            if(fragColor.r == 0.){
