void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    float dMin = 1000.;

    vec2 resizedUv = fragCoord;
    float stepSize  = iResolution.x / 3.;
    int cId = int(floor(fragCoord.x/stepSize));
    resizedUv.x = mod(resizedUv.x, stepSize) * 3.;

    fragColor = texture(iChannel3, fragCoord/iResolution.xy);

    if(fragColor.a < 0.2){
        // initialize

        vec2[3] origins;
        origins[0] = vec2(iResolution.y)/2.; // head
        origins[1] = origins[0]+ vec2(-32, 32); // left ear
        origins[2] = origins[0] + vec2(32, 32); // right ear

        float[3] r;
        r[0] = 36.; r[1] = 18.; r[2] = 18.;// head radius and ear radius 
        float edge = H/2.; // weight of edge
        bool flag = false;
        for(int i = 0; i < 3; i++){  
            float p2ori = length(resizedUv - origins[i]);
            if(p2ori < r[i] + edge){
                flag = true;
                if(p2ori < r[i]){
                    fragColor = vec4(1.0, 1.0, 1.0, 2.);
                    return;
                }
                else fragColor = vec4(0.0, 0.0, 0.0, 2.); 
            }
        }
        if(flag)return;
    }
    if(fragColor.a > 1.0){// in shape
        return;
    }
    for (int n = 0; n < int(txSize); n++){
        vec4 pPos = LoadParticle(n, cId);
        if(pPos.x > 0.){
            dMin = min (dMin, length (resizedUv - pPos.xy));
        }
    }

    vec4 col = dMin < H/2.? vec4(1., 0, 0, 1.): vec4(0, 0, 0, 0.2);
        //vec3(dMin);//mix (vec3 (0.2),  vec3 (0., 1., 0.), 1. - smoothstep (0.4, 0.5, dMin));

    fragColor = col;
}

