// Also looking into this one: 					     https://www.shadertoy.com/view/tl23Rm
// Shadows: Maarten 								 https://www.shadertoy.com/view/4dfXDn

vec4 loadValue( in ivec2 dataIndex )
{
    return texelFetch( iChannel0, dataIndex, 0 );
}

vec4 Volume(vec3 pos){
	float hmix;

    vec4 bakeUV = SampleVolume(pos, GetVolumeHSlices(iResolution.y), hmix);

    return mix( texture(iChannel0, bakeUV.xy), texture(iChannel0, bakeUV.zw), hmix);
}

vec3 getFishEye(vec2 uv, float level) {

    float len = length(uv);
    float a = len * level;
    vec3 fishUV = vec3(uv / len * sin(a), cos(a));

    vec3 camForward = 		loadValue(CAM_FORWARD).xyz;
    vec3 camRight = normalize(cross(camForward, vec3(0.0, 1.0, 0.0))); 
    vec3 camUp = normalize(cross(camRight, camForward));

    return (fishUV.x * camRight) + (fishUV.y * camUp) + (fishUV.z * camForward);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 cameraPosition = loadValue(CAM_POS_PRSST).xyz;
	vec3 rayDirection = getFishEye(uv,  1.5);

    vec2 rayCast = GetRaycastDist(cameraPosition, rayDirection);

   // rayCast.x = 0.; // DEBUG

    vec3 col = vec3(0);

    if (rayCast.x<MAX_DIST){

        float dist = max(0.,rayCast.x);
        float steps;

        float mxDist = 
            //MAX_DIST;
            rayCast.y;

        GetRayMarchDist(cameraPosition, rayDirection, mxDist, dist, steps);

        steps /= 30.; // DEBUG

         if(dist<mxDist) {
            vec3 hitPos = cameraPosition + rayDirection * dist;
            vec3 n = GetNormal(hitPos, dist);
            float dif = dot(n, normalize(vec3(1,2,3)))*.5+.6;
            col += dif;  

            #ifdef DEBUG
             col.r =  steps; 
            #endif

             vec3 safeHitPos = hitPos + n * 0.001*dist; // To start other calculations not trom inside the object

             float shadow = Shadow(safeHitPos, lightSource_0.xyz, SHADOW_RADIUS, lightSource_0.w);

             vec4 baked = Volume(safeHitPos + n*VOLUME_SCALE * .3);

             col.rgb *= (shadow * lightSource_0_Color + baked.b * skyColor);



         } else {
              col.rgb = 
             #ifdef DEBUG
                 vec3(steps,0.,0.);
             #else
             	 SkyBox(rayDirection);
             #endif
         }


        //float remapped = sharpstep(0., 2., rayCast.y);
      	//col.rgb = vec3(0., remapped, remapped);

         col.rgb = max(vec3(0.), col.rgb);
         col = pow(col, vec3(.4545));	// gamma correction
         vec3 mixed = col.gbr + col.brg;

    col.rgb += mixed*mixed *  .2;
   } else {
    	col.rgb = SkyBox(rayDirection); 
   }


    // Volumetric lights 

    const float VOL_STEPS = 10.;

    const float VOL_STEP_SIZE = 3. * VOLUME_SCALE/ VOL_STEPS;

    const float FOG_DENSITY = .25/VOL_STEPS;

    vec4 ambientLight = vec4(0., 0., 0., FOG_DENSITY);

    float volDist = VOL_STEP_SIZE;

    for (float i=0.; i<VOL_STEPS; i+=1.)
    {
    	vec3 volumePosition = cameraPosition + rayDirection * volDist;

        vec3 volCol = Volume(volumePosition).rgb;

        float smoothAdd = smoothstep(rayCast.x, rayCast.x*0.7, volDist);

        float adding = FOG_DENSITY * smoothAdd;

        ambientLight.rgb += volCol * adding / ambientLight.a;

        ambientLight.a += adding;

        volDist += VOL_STEP_SIZE * (1. + i*i/VOL_STEPS); // volDist*1.6;

        i+= (1. - smoothAdd) * 100.;
    }

    ambientLight.a = min(1., ambientLight.a);

    ambientLight.rgb =  
        lightSource_0_Color.rgb *  (0.05 * lightSource_0.w * ambientLight.r) +
        skyColor * ambientLight.b * 0.2
        ;

    col.rgb = mix(col.rgb, ambientLight.rgb , ambientLight.a);

    fragColor = vec4(col,1.0);
}

