const int SPHERE_CNT = 7;

vec4 lightSource_0 = vec4(70., 60., 100., 20.);

vec3 lightSource_0_Color = vec3(.8, .8, .1);

vec4[SPHERE_CNT] GetSpheres(){
	vec4  spheres[SPHERE_CNT];
    spheres[0] = vec4(30.,	80.,		5., 	32.5);
    spheres[1] = vec4(60.,	15.,		-29., 	17.);
    spheres[2] = vec4(10.,	0.,		45., 	22.5);
    spheres[3] = vec4(35.,	0.,		50., 	31.);
    spheres[4] = vec4(60.,	1.,		65., 	9.);
    spheres[5] = vec4(100.,	15.,		5., 	22.5);
    spheres[6] = vec4(110.,	45.,	9., 	31.);
    return spheres;
}

const int BOX_CNT = 3;

vec4[BOX_CNT] GetBoxes(){
	vec4  boxes[BOX_CNT];
    boxes[0] = vec4(-28.,	56.,	5., 	38.5);
    boxes[1] = vec4(100.,	21.,	39., 	25.);
    boxes[2] = vec4(380.,	-370.,	15., 	360.);

    return boxes;
}

const int TOTAL_SHAPE_VARIATION = 2;
const int TOTAL_SHAPES = SPHERE_CNT+BOX_CNT;

vec2 GetRaycastDist(vec3 rayPos, vec3 rayDir)
{

    vec2 nearestCast = vec2(MAX_DIST+1.,MAX_DIST+1.);

    // SPHERES
    int raycastIndex = 0;
    vec4  spheres[SPHERE_CNT] = GetSpheres();

    for(int i=0; i<SPHERE_CNT; i++)
    {
        vec4 s = spheres[i];
        IntersectSphere(rayPos, rayDir, s.xyz , s.w, nearestCast);
    }

    // BOXES
    vec4  boxes[BOX_CNT] = GetBoxes();
    vec3 precalculateM = sign(rayDir)/max(abs(rayDir), 1e-8);

    for(int i=0; i<BOX_CNT; i++)
    {
        vec4 b = boxes[i];
        iBox(rayPos - b.xyz, precalculateM, vec3(b.w,b.w,b.w), nearestCast);
    }

    return nearestCast;
}


float GetDist(vec3 p) {

    vec4  spheres[SPHERE_CNT] = GetSpheres();

    float sphereDist = MAX_DIST; 

    for(int i=0; i<SPHERE_CNT; i++)
    {
        vec4 s = spheres[i];
        float dist = sdSphere(p - s.xyz, s.w);
        sphereDist = 
            smAdd(sphereDist, dist, 1.);
            min(dist, sphereDist);
    }

   float gyr = sdGyroid(p, .7, 1., .01);

   sphereDist = smMinus(sphereDist, gyr, 0.4);


    vec4  boxes[BOX_CNT] = GetBoxes();

    for(int i=0; i<BOX_CNT; i++)
    {
       vec4 b = boxes[i];
       float dist = sdBox(p - b.xyz, vec3(b.w,b.w,b.w));

       sphereDist = smAdd(sphereDist, dist, 10.);
           min(dist, sphereDist);
    }

    return sphereDist;
}

vec3 GetNormal(vec3 p, float dist ) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}


float Shadow(vec3 p, vec3 pos, float radius, float lightPower)
{
	vec3 rayDir = normalize(pos - p);
	float maxDist = length(p - pos);

	// fraction of light visible, starts at one radius (second half added in the end);
	float lf = radius * maxDist;

    float minStep = radius * 0.15;

	// distance traveled
	float dt =minStep;

    float sd = 0.;

    float fullShadow = 0.;

	for (float i = 0.; i < 32.; i+=1.)
	{				
		sd = GetDist(p + rayDir * dt);

		lf = min(lf, sd / dt);

		// move ahead
		dt += max(minStep, abs(sd));

        fullShadow = step(sd, -radius);

        i+= (fullShadow + step(maxDist,dt))* 100.;
	}

	lf = clamp((lf*maxDist + radius) / (2.0 * radius), 0.0, 1.0);
	lf = smoothstep(0.0, 1.0, lf);

	return lf * lightPower / maxDist ;
}



void GetRayMarchDist(vec3 rayPos, vec3 rayDir, float maxDist, inout float dist, out float steps){

    steps = 0.;

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = rayPos + rayDir*dist;
        float dS = GetDist(p);
        dist += dS;
        #ifdef DEBUG
        steps += 1.; // DEBUG
        #endif
        if(dist>maxDist || abs(dS)<SURF_DIST*(1.+dist))
        { 
            break;
        }
    }

}

