// This buffer handles camera movement
// ASCII: https://www.ascii-code.com/

const float KEY_E            = 69.5 / 256.0;
const float KEY_Q            = 81.5 / 256.0;
const float KEY_W            = 87.5 / 256.0;
const float KEY_A            = 65.5 / 256.0;
const float KEY_S            = 83.5 / 256.0;
const float KEY_D            = 68.5 / 256.0;
const float KEY_LEFT         = 37.5 / 256.0;
const float KEY_UP           = 38.5 / 256.0;
const float KEY_RIGHT        = 39.5 / 256.0;
const float KEY_DOWN         = 40.5 / 256.0;
const float KEY_SHIFT        = 16.5 / 256.0;
const float KEY_SPACE 	     = 32.5 / 256.0;

vec4 loadValue( in ivec2 dataIndex )
{
    return texelFetch( iChannel0, dataIndex, 0 );
}

vec3 volumeUVtoWorld(vec2 uv, vec4 VOLUME_H_SLICES) {

	float hy = floor(uv.y*VOLUME_H_SLICES.x);
	float hx = floor(uv.x*VOLUME_H_SLICES.x);

	vec2 xz = uv * VOLUME_H_SLICES.x;

	xz.x -= hx;
	xz.y -= hy;

	xz = (xz*2. - 1.) *VOLUME_H_SLICES.y;

	float h = hy * VOLUME_H_SLICES.x + hx;

	vec3 bsPos = vec3(xz.x, h, xz.y) / VOLUME_POSITION_N_SIZE.w;

	vec3 worldPos = VOLUME_POSITION_N_SIZE.xyz + bsPos;

	return worldPos;
}

vec4 Quat(vec3 axis, float angle)
{
    angle *= 0.5;
    return normalize(vec4(axis * sin(angle), cos(angle)));
}

vec3 Rotate(vec4 q, vec3 v)
{
    vec3 t = 2.0 * cross(q.xyz, v);
    return v + (q.w * t) + cross(q.xyz, t);
}

vec4 QxQ(vec4 q1, vec4 q2)
{
    vec4 res = vec4(0.);
    res.w   = (q1.w * q2.w) - dot(q1.xyz, q2.xyz);
    res.xyz = (q1.w * q2.xyz) + (q2.w * q1.xyz) + cross(q1.xyz, q2.xyz);
    return normalize(res);
}

vec2 GetMouseDelta()
{
        vec4 mousePrevious = loadValue( CAM_MOUSE_PREV_PRSST);
        float downPrevious = mousePrevious.z; 
        float down = clamp(iMouse.z * 800., 0. , 1.);
        return down * downPrevious *  (iMouse.xy/iResolution.xy-mousePrevious.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    if (fragCoord.y<=PRSST_MAX_Y) {

      fragColor = texture(iChannel0, fragCoord/iResolution.xy);

       ivec2 ifragCoord = ivec2( fragCoord-0.5 );

       #define WRITE_INDEX(data) inStoreArea(data, ifragCoord)  
       #define PRSST_RANGE(data1, data2) inStoreRange(data1, data2, ifragCoord)  


        vec4 gameState = loadValue(GAME_STATE);

        if (gameState.x == 0.)
        {

            if (WRITE_INDEX(CAM_FORWARD)){
             	fragColor = vec4(vec3(0.,0.,1.), 0.0);
                return;
            }

            if (WRITE_INDEX(GAME_STATE)){
             	fragColor = vec4(1., 0., 0. , 0.0);
                return;
            }

            if (WRITE_INDEX(CAM_ROTATION_PRSST))
       		{
                fragColor = vec4(0., 0., 0., 1.0);
                return;
            }

            discard;
        }

        // Camera persistance: 

        if (WRITE_INDEX(CAM_FORWARD)) {

             vec4 lastQuat    = loadValue(CAM_ROTATION_PRSST);
            vec2 mouseDelta  = GetMouseDelta() * MOUSE_SENSITIVITY;

            vec3 forward = loadValue(CAM_FORWARD).xyz;
            vec3 right   = normalize(cross(forward, vec3(0.,1.,0.)));
            vec3 up      = normalize(cross(right, forward));

            lastQuat = QxQ(Quat(up, -mouseDelta.x), lastQuat);
            lastQuat = QxQ(Quat(right, mouseDelta.y), lastQuat);
            fragColor = lastQuat;

            fragColor = vec4(Rotate(lastQuat, vec3(0.,0.,1.)), 0.0);

       } else  if (WRITE_INDEX(CAM_ROTATION_PRSST)) {

            vec4 lastQuat    = loadValue(CAM_ROTATION_PRSST);
            vec2 mouseDelta  = GetMouseDelta() * MOUSE_SENSITIVITY;

            vec3 forward = loadValue(CAM_FORWARD).xyz;
            vec3 right   = normalize(cross(forward, vec3(0.,1.,0.)));
            vec3 up      = normalize(cross(right, forward));

            lastQuat = QxQ(Quat(up, -mouseDelta.x), lastQuat);
            lastQuat = QxQ(Quat(right, mouseDelta.y), lastQuat);
            fragColor = lastQuat;

       } else  if (WRITE_INDEX(CAM_MOUSE_PREV_PRSST)) {

           vec4 mousePrevious = loadValue( CAM_MOUSE_PREV_PRSST);
           mousePrevious.xy = iMouse.xy/iResolution.xy;
           mousePrevious.z = clamp(iMouse.z * 800., 0. , 1.);
           fragColor = mousePrevious;


       } else if (WRITE_INDEX(CAM_POS_PRSST)) {

 			vec4 camera = loadValue(CAM_POS_PRSST);


             vec3 off;

            off.x -=  texture( iChannel1, vec2(KEY_A,0)).x;
            off.x +=  texture( iChannel1, vec2(KEY_D,0)).x;
            off.z +=  texture( iChannel1, vec2(KEY_W,0)).x;
            off.z -=  texture( iChannel1, vec2(KEY_S,0)).x;
            off.y +=  texture( iChannel1, vec2(KEY_E,0)).x;
            off.y -=  texture( iChannel1, vec2(KEY_Q,0)).x;

            if (length(off)>0.0001){

                off = normalize(off);

                vec3 camForward = 	loadValue(CAM_FORWARD).xyz;
    			vec3 camRight = normalize(cross(camForward, vec3(0.0, 1.0, 0.0))); 

                vec3 add = camForward * off.z + camRight * off.x;

                float run = texture( iChannel1, vec2(KEY_SHIFT,0)).x;

                float dist = min(1., GetDist(camera.xyz) / SLOWDOWN_RANGE);

                camera.xyz += (vec3(add.x,0.,add.z) + vec3(0., 1., 0.) * off.y) * max(0.1, dist)  * mix(PLAYER_SPEED, PLAYER_RUN_SPEED, run);

                if (dist<PLAYER_SIZE)
                {
                    camera.xyz += GetNormal(camera.xyz, 1.) * (PLAYER_SIZE - dist);
                }
            }


            fragColor = camera;
       }
    } else {

        // Ambient Light Baking

        vec2 uv = fragCoord/iResolution.xy;

        fragColor = texture(iChannel0, uv);

        vec3 pos = volumeUVtoWorld(uv, GetVolumeHSlices(iResolution.y));

        vec3 randomDirection = normalize(hash33(pos + iTime));

        pos += randomDirection* VOLUME_SCALE * 0.5;

        float dist = GetDist(pos);

        if (dist<0.)
            return;

        vec3 addColor = vec3(0.);

        // SKY LIGHT

        vec2 rayCast = GetRaycastDist(pos, randomDirection);

        if (rayCast.x + 1. > MAX_DIST) 
        {
        	addColor.b = 1.;  //SkyBox(randomDirection);
        }

        // LIGHT POINT

        vec3 toLight = lightSource_0.xyz- pos;
        float len = length(toLight);

        #ifdef MARCH_VOLUME_SHADOW
        	float visible =  Shadow(pos, lightSource_0.xyz, SHADOW_RADIUS, lightSource_0.w);
        #else
         	vec2 lightPointRay = GetRaycastDist(pos, normalize(toLight));
        	float visible = step(0., dist) * step(length(toLight), lightPointRay.x + 0.01);
        #endif


        addColor.r += visible;


        //addColor += lightSource_0_Color.rgb *  ( lightSource_0.w * visible * 1000. / (len*len + 1.));

        // STORING

        fragColor.a += 1.;

        float newPortion = 1./ (fragColor.a);

        fragColor.a = min(50., fragColor.a);

        fragColor.rgb = mix(fragColor.rgb, addColor, newPortion);
    }
}

