#define iR iResolution

vec2 Hash12(float f){ 
    return vec2(fract(sin(65.621*f+f) * 43758.5453),fract(sin(65.621*f-f) * 231.654));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	float scale = iR.x/iR.y;
    vec2 uv = (fragCoord)/iR.x*scale;

    float seed = 621.;

    int numberOfPoints = 200;
    vec2 points[200];
    vec2 offset;

   	for(int i = 0;i<numberOfPoints-1;i++){
        seed += float(i);
    	vec2 xy = Hash12(seed);
        offset = vec2(sin(xy.x+iTime+float(i)*45.),sin(xy.y+iTime+float(i)*45.))/100.;
        xy += offset;
        xy.x *= scale;
        points[i] = xy;
    }

    vec3 col = vec3(0,0,0);
    float t = iMouse.x/iR.x;
    float mind = 9999.;
    int pid = 0;
    float p = 0.;

    vec3 hsvcol = vec3(0,.8,.9);
    for(int i = 0;i<numberOfPoints-1;i++){
        float e = euclidDist(uv,points[i]+offset);
        float m = manhattanDist(uv,points[i]+offset);

        if(e<.005) p = 1.;

        float d = m*t + e*(1.-t);
        if(d<mind) {
            pid = i;
            mind = d;
        }
    }
    hsvcol.x = float(pid)/float(numberOfPoints-1);
    col+=hsv2rgb(hsvcol);
    col*=(1.-mind)*(1.-mind)*(1.-mind)*(1.-mind);
    col-=p;

    fragColor = vec4(col,1.0);
}

common_code:
float manhattanDist(vec2 a, vec2 b){
	return abs(a.x - b.x) + abs(a.y - b.y);
}

float euclidDist(vec2 a, vec2 b){
	return sqrt(pow(a.x-b.x,2.) + pow(a.y-b.y,2.));
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

