const int WW = 0xFCFCFC; //WILSON WHITE;
const int B  = 0x2C2C80; //BLUE;
const int DG = 0x003820; //DARK GREEN;
const int BL = 0x1474B4; //BALMORAL BLUE;
const int WG = 0x408060; //WTHRD GREEN;
const int HG = 0x285800; //HUNTING GREEN;
const int DR = 0x880000; //DARK RED;
const int P  = 0x780078; //PURPLE;
const int DB = 0x1C1C50; //DARK BLUE;
const int W  = 0xE0E0E0; //WHITE;
const int H  = 0x006428; //BOTTLE;
const int DB2 = 0x202060; //DARK BLUE;
const int LG = 0x289C18; //LIGHT GREEN;
const int AB = 0x1870A4; //ANC BLUE;
const int LN = 0xC0C0C0; //LIGHT GREY;
const int RB = 0x1C0070; //ROYAL BLUE;

int tfactor = 1; // Multiply threadcount
int scale = 1;

// The setts

// Buchanan (1850) - an asymmetric sett
// B18G46K6B18K6Y40K6Y40K6B18K6R40W6R40K6B18K6G46
// Pallet:
// K=101010BLACK;B=2C2C80BLUE;G=006818GREEN;R=C80000RED;W=E0E0E0WHITE;Y=E8C000YELLOW;
const int buchanan[] =
  int [](B,18,G,46,K,6,B,18,K,6,Y,40,K,6,Y,40,K,6,B,18,K,6,R,40,W,6,R,40,K,6,B,18,K,6,G,46);

// Hunting Stewart - an asymmetric sett.
// B9HG4B9K3B3K3B3K8HG27R4HG27K8HG5K13HG4K13HG5K8HG27Y4HG27K8B3K3B3K3
// G=006818GREEN;K=101010BLACK;B=2C2C80BLUE;DG=003820DARK GREEN;
// R=C80000RED;Y=E8C000YELLOW;BL=1474B4BALMORAL BLUE;
// WG=408060WTHRD GREEN;HG=285800HUNTING GREEN;

// No reflection and odd threadcounts.
const int huntingstewart[] = int[](B,9,HG,4,B,9,K,3,B,3,K,3,B,3,K,8,HG,27,R,4,HG,27,K,8,HG,5,
                                   K,13,HG,4,K,13,HG,5,K,8,HG,27,Y,4,HG,27,K,8,B,3,K,3,B,3,K,3);

// Most other tartans are reflected: halve end threadcounts to get correct reflection

// Fraser (1745)
// R4B24R4G24R48WW2
// G=006818GREEN;WW=FCFCFCWILSON WHITE;B=2C2C80BLUE;R=C80000RED;

const int fraser[] = int[](R,4/2,B,24,R,4,G,24,R,48,WW,2/1);

// Macbeth
// B144 Y16 K20 WW4 K4 WW4 K8 G32 R24 K4 R12 WW4
const int macbeth[] = int[](B,144/2,Y,16,K,20,WW,4,K,4,WW,4,K,8,G,32,R,24,K,4,R,12,WW,4/2);

// Farquharson
// R4 B16 K4 B4 K4 B4 K32 G32 Y8 G32 K32 B32 K4 R8
const int farquharson[] = int[](R,4/2,B,16,K,4,B,4,K,4,B,4,K,32,G,32,Y,8,G,32,K,32,B,32,K,4,R,8/2);

// "Loud MacLeod"
// K32Y4K32Y48R4
// K=101010BLACK;R=C80000RED;Y=D8B000YELLOW;
const int macleod[] = int[](K,32/2,Y2,4,K,32,Y2,48,R,4/2);

// Fujitsu
// Y2K12G64K24DR24P18DB12W2
// Y=E8C000YELLOW;K=101010BLACK;G=006818GREEN;DR=880000DARK RED;P=780078PURPLE;DB=1C1C50DARK BLUE;W=E0E0E0WHITE;
const int fujitsu[] = int[](Y,2/2,K,12,G,64,K,24,DR,24,P,18,DB,12,W,2/2);

// Asda
// "Winning design in a competition run by ASDA Wal-mart in Elgin,
