const int asda0[] = int[](LG,136,R,8,LG,12,DB2,8,H,68,DB,8,LG,12,R,8,LG,136,DB2,8,H,32,R,8,H,38,DB,8);
const int asda1[] = int[](LG,248,Y,12,H,28,Y,12);

// The FBI Tartan
// "Commissioned for use for the FBI Pipe Band from Thomas
// Gordon & Sons of Glasgow but now regarded as being the FBI
// tartan".

// DR4AB52RB32LN6RB4LN4RB12
// AB=1870A4ANC BLUE;DR=880000DARK RED;LN=C0C0C0LIGHT GREY;RB=1C0070ROYAL BLUE;

const int fbi[] = int[](DR,4/2,AB,52,RB,32,LN,6,RB,4,LN,4,RB,12/2);

// Black Watch Plaid of Pipers
// DB24K4R4K4R4K24DG22Y4DG22K24DB22K4R4
// K=101010BLACK; DG=006818GREEN; R=C80000RED; Y=E8C000YELLOW; DB=202060DARK BLUE;
// DG here is just G, DB is DB2
const int blackwatchpiper[] = int[](DB2,24/2,K,4,R,4,K,4,R,4,K,24,G,22,Y,4,G,22,K,24,DB2,22,K,4,R,4/2);

// Thomson Dress Blue
// R6AB60K12W24K24Y6
// AB=1870A4ANC BLUE;K=101010BLACK;R=C80000RED;W=E0E0E0WHITE;Y=E8C000YELLOW;
const int thomson[] = int[](R,6/2,AB,60,K,12,W,24,K,24,Y,6/2);

// St. Piran's Cornish Flag
// R4K2W20K40W10K40W20K2 - full sett
// K=101010BLACK;R=C80000RED;W=E0E0E0WHITE;
const int stpirans[] = int[](R,4,K,2,W,20,K,40,W,10,K,40,W,20,K,2);

int settlength = 0;  // Total number of bands
int settsize = 0;    // Total number of threads
bool reflect = true; // Is sett reflected?

const int NSETTS = 9;

// Index into the sett.
int sett(int settid,int i) {
  int k = 0;
  if (settid == k++) return buchanan[i];
  if (settid == k++) return huntingstewart[i];
  if (settid == k++) return macbeth[i];
  if (settid == k++) return macleod[i];
  if (settid == k++) return fraser[i];
  if (settid == k++) return thomson[i];
  //if (settid == k++) return farquharson[i];
  if (settid == k++) return blackwatchpiper[i];
  //if (settid == k++) return fujitsu[i];
  if (settid == k++) return stpirans[i];
  if (settid == k++) return fbi[i];
  return 0;
}

// Set up the sett.
bool setupsett(int settid) {
  if (settid < 0 || settid >= NSETTS) return false;
  // Keep list of tartans in the two blocks consistent.
  int k = 0;
  if (settid == k++) settlength = buchanan.length(),reflect = false;
  if (settid == k++) settlength = huntingstewart.length(),reflect = false;
  if (settid == k++) settlength = macbeth.length();
  if (settid == k++) settlength = macleod.length();
  if (settid == k++) settlength = fraser.length();
  if (settid == k++) settlength = thomson.length();
  //if (settid == k++) settlength = farquharson.length();
  if (settid == k++) settlength = blackwatchpiper.length();
  //if (settid == k++) settlength = fujitsu.length();
  if (settid == k++) settlength = stpirans.length(),reflect = false;
  if (settid == k++) settlength = fbi.length();
  if (k != NSETTS) return false;

  for (int i = 0; i < settlength; i += 2) {
    settsize += tfactor*sett(settid,i+1);
  }
  return true;
}

int mymod(int n, int m) {
  // glsl % undefined for -ve arguments
  if (n < 0) return m-1-(-n-1)%m;
  else return n%m;
}

int getsettcol(int settid,int n) {
  if (reflect) {
    n = mymod(n,2*settsize);
    n = min(n,2*settsize-n-1);
  } else {
    n = mymod(n,settsize);
  }
  int total = 0;
  for (int i = 0; i < settlength; i += 2) {
    total += tfactor*sett(settid,i+1);
    if (n < total) return sett(settid,i);
  }
  return 0;
}

vec3 hex2rgb(int n) {
  return vec3((n>>16)&0xFF,(n>>8)&0xFF,(n>>0)&0xFF)/256.0;
}

vec3 getcol(int settid,ivec2 a) {
  // Determine if we have a warp or weft thread at this location
  bool warp = mymod(a.x-a.y,4) < 2;
  int n =  warp ? a.x : a.y;
  // And look up in the sett.
  vec3 col = hex2rgb(getsettcol(settid,n));
  if (!warp) col *= 0.9;
  return col;
}

const int CHAR_D = 68;
const int CHAR_F = 70;
const int CHAR_H = 72;
const int CHAR_M = 77;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int CHAR_U = 85;

const int KEY_PAGE_UP = 33;
const int KEY_PAGE_DOWN = 34;
const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

bool keypress(int key) {
  return texelFetch(iChannel0,ivec2(key,2),0).x != 0.0;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  tfactor = 1+max(0,keycount(KEY_PAGE_UP)-keycount(KEY_PAGE_DOWN));
  scale = 1+max(0,keycount(KEY_UP)-keycount(KEY_DOWN));

  int settid = keycount(KEY_RIGHT)-keycount(KEY_LEFT);
  settid = mymod(settid,NSETTS+1);
  if (settid == 0) settid = mymod(int(0.5*iTime),NSETTS);
  else settid--;

  if (!setupsett(settid)) {
    fragColor = vec4(1);
    return;
  }
  vec2 p = fragCoord-0.5*iResolution.xy;
  if (!keypress(CHAR_M) && iMouse.x > 0.0) {
    p -= iMouse.xy-0.5*iResolution.xy;
  }
  p /= float(scale);
  vec3 col = getcol(settid,ivec2(floor(p)));
  float value = 1.0;
  float saturation = 1.0;
  if (keypress(CHAR_M) && iMouse.x > 0.0) {
    value = iMouse.x/iResolution.y;
    saturation = iMouse.y/iResolution.y;
  }
  // Unsaturate by mixing with tan for a "weathered" look.
  vec3 satcol = hex2rgb(0xD2B48C);//hex2rgb(0x4A412A);
  if (!keypress(CHAR_H) || fragCoord.x < 0.5*iResolution.x) {
    col = value*(mix(satcol,col,saturation));
  }
  fragColor = vec4(col,1);
}

