// testing some 2d shape extrusion

#define AA 2
#define PI	3.1415926
#define TAU 6.2831853

// 2d distance functions by IQ
float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, in float rb )
{
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x>scb.x*p.y) ? dot(p.xy,scb) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdPie( in vec2 p, in vec2 c, in float r )
{
    p.x = abs(p.x);
    float l = length(p) - r;
    float m = length(p-c*clamp(dot(p,c),0.0,r)); // c = sin/cos of the aperture
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float sdHorseshoe( in vec2 p, in vec2 c, in float r, in vec2 w )
{
    p.x = abs(p.x);
    float l = length(p);
    p = mat2(-c.x, c.y, 
              c.y, c.x)*p;
    p = vec2((p.y>0.0)?p.x:l*sign(-c.x),
             (p.x>0.0)?p.y:l );
    p = vec2(p.x,abs(p.y-r))-w;
    return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float pizza( vec2 p, vec2 c, float rad1, float rad2)
{
    p.x = abs(p.x);

    vec2 c2 = vec2(c.y,-c.x);

    float l = -dot(c,p.xy);
    float q = p.x+c.x*l;
    float l2;

    if (max(q,-sign(p.y*c.y)) < 0.0) {
        l = -length(p.xy);
    }

    if (l < 0.0) {
        l2 = length(p.xy);
    } else {
        l2 = dot(c2,p.xy)*sign(c.y);
    }



    vec2 a = vec2(max(l2-rad2,rad1-l2),l);

    return length(max(a,0.0))+min(0.0,max(a.x,a.y));
}


float map( in vec3 pos )
{
    float fill = (0.5+sin(iTime)*0.5)*PI;

    vec2 uv = pos.xz;

    // arc
    float ta = radians(90.0);
    float tb =  fill;
    float rb = 0.1;
    float d1 = sdArc(uv-vec2(0.0,1.5),vec2(sin(ta),cos(ta)),vec2(sin(tb),cos(tb)), 0.5, rb);  

    // pie
    float d2 = sdPie(uv,vec2(sin(tb),cos(tb)),0.5);

    // horseshoe
    float t = 3.14-fill;
    vec2  w = vec2(0.0,0.1);
    //float d3 = sdHorseshoe(uv+vec2(0.0,1.5),vec2(cos(t),sin(t)), 0.5, w);    

    // pizza!


	float d3 = pizza(uv+vec2(0.0,1.5),vec2(cos(t),sin(t)),0.4,0.5);


    d1=min(d1,d2);
    d1=min(d1,d3);
    d1 = opExtrussion(pos.zxy,d1,0.1);
    return d1;
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;

     // camera movement	
    float an = sin(iTime)*0.5;
    float y = 2.;
    float dist=1.5;

    if (iMouse.z>0.5)
    {
        an=iMouse.x/iResolution.x*4.0;
        y = (iMouse.y/iResolution.y)*8.0;
        y-=4.0;
        dist=3.0;
    }

	vec3 ro = vec3( dist*cos(an), y, dist*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 8.0;
        float t = 0.0;
        for( int i=0; i<80;i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting	
        vec3 col = vec3(0.6,0.4,0.4);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            vec3 ambcol = vec3(0.4,0.3,0.3)*amb;
            col = ambcol + vec3(0.6,0.55,0.85)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

