    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdGyroid(vec3 p, float scale, float thickness, float bias) {
	p *= scale;
    return abs(dot(sin(p), cos(p.zxy))+bias)/scale - thickness;
}

float GetDist(vec3 p) {
    p.xy*=Rot(iTime*0.06);
    p.zx*=Rot(iTime*0.06);
    float box = sdBox(p, vec3(1.0));

    // return box;
    float gyro = sdGyroid(p,5.+abs(5.0*cos(iTime*0.065)),0.125,2.0)/2.0;
    return max(box, gyro*4.);
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    //uv.x = abs(uv.x);
   uv.y = abs(uv.y);
	vec2 m = vec2(cos(iTime*1.),iMouse.x)/iResolution.xy;

    vec3 col = vec3(0);
    //float amp = texture(iChannel0, vec2(1., 0.0)).r*4.3;
    float pos = atan(uv.y/abs(uv.x))/3.14 + 0.5;
    float amp = texture(iChannel0, vec2(pos, 0.0)).x*(pos*0.3+0.1)*10.0;
    vec3 ro = vec3(sin((iTime)*0.096), 1.+cos(iTime*0.1), 2.-cos((iTime+ amp)*0.36));
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);

    	float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
    	col += dif;  
    }

    col = pow(col, vec3(0.1 + abs(0.5 * cos(iTime*0.1))));	// gamma correction
    col += texture(iChannel1, uv*10.-iTime*.3).rrr * 0.5;
    fragColor = vec4(1. - col,1.0);
}

vec4 getdata(int index) {
    ivec2 p;
    p.x = index%int(iResolution.x);
    p.y = index/int(iResolution.x);
    return texelFetch(iChannel0,p,0);
}

float shape(vec3 p,float s, float r) {


    vec3 p2 = abs(p);
    //q.x is the smallest, q.z is the biggest, q.y is in the middle
    //q reflects the sdf so i can reduce calculations
    vec3 q = vec3(min(min(p2.x,p2.y),p2.z),0,max(max(p2.x,p2.y),p2.z));
    q.y = p2.x+p2.y+p2.z-q.x-q.z;

    float l = q.z-s-r;
    l = max(l,(p2.x+p2.y+p2.z-s*3.0-r)/sqrt(3.0));
    l = max(l,((q.y+q.z-s*2.0-r)/sqrt(2.0)));

    if (l < 0.0 || q.y < s) return l;

    if (q.y-q.z+r<0.0)
    {
        float a = length(max(q.xy-s,vec2(0)));
        l = length(vec2(q.z-r-s,a));
    }
    else if (q.x > s&&l>0.0)
    {
        float tri = max(dot(q,vec3(-2.0, 1.0, 1.0))-r,0.0);

        tri /= sqrt(6.0);

        float tric = length(vec2(tri,(dot(p2,vec3( 1.0, 1.0, 1.0))-r-s*3.0)/sqrt(3.0)));

        l = tric;
    }

    return l;
}

float scene(vec3 p) {

    vec3 q = mod(p,4.0)-2.0;

    return abs(shape(q,sin(iTime)*0.5+0.5, cos(iTime)*0.5+0.5)-0.2);
}

float sdftest(vec3 p) {
    vec3 sph = getdata(sphere).xyz;

    if (sph == vec3(0)) return FAR;

    float size = scene(sph);

    return length(p-sph)-size;
}

float map(vec3 p) {

    float l = scene(p);

    l = min(l,abs(sdftest(p)));

    return l-0.001;
}

int getsurface(vec3 p) {

    if (scene(p) < 0.002) return 1;

    return 2;
}

vec3 getnormal(vec3 p) {
    vec2 e = vec2(0,0.001);

    return normalize(vec3(
        map(p+e.yxx)-map(p-e.yxx),
        map(p+e.xyx)-map(p-e.xyx),
        map(p+e.xxy)-map(p-e.xxy)));
}

float march(vec3 ro, vec3 rd) {

    float d = 0.0;

    for (int i = 0; i < 1000; i++) {
        float l = map(ro+rd*d);

        if (l < 0.001 || d > FAR) {
            return d;
        }

        d += l;
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec3 ro = getdata(campos).xyz;
    vec3 rd = normalize(vec3(uv,1));

    vec4 rotcam = getdata(camrot);

    rd.zy *= rot(rotcam.y);
    rd.zx *= rot(rotcam.x);

    float l = march(ro,rd);

    vec3 col = -rd*0.25+0.25;

    if (l < FAR) {
        vec3 p = ro+rd*l;
        int id = getsurface(p);
        vec3 n = getnormal(p);

        float a = max(0.1,dot(n,normalize(vec3(1))));

        a /= (l*l*0.005+1.0);

        if (id == 1) {
            col = n*0.5+0.5;
        } else {
            col = vec3(a);
        }

        float b = max(scene(p),sdftest(p));
        b = max(1.0-b*b*1000.0,0.0);

        col = mix(col,vec3(1),b);
    }

    fragColor.rgb = pow(col,vec3(1.0/2.2));    

}

