vec4 updatedata(int index) {
    vec4 data = getdata(index);

    vec4 iMouse2 = getdata(lastmouse);


    bool refresh = iFrame == 0;

    if (index == lastmouse) {
        return iMouse;
    } else if (index == campos) {

        if (refresh) {
            return vec4(vec3(0),1);
        }

        vec4 rotcam = getdata(camrot);

        vec3 move = vec3(getkey(MOVE_RIGHT,0)-getkey(MOVE_LEFT,0),
                         getkey(MOVE_UP   ,0)-getkey(MOVE_DOWN,0),
                         getkey(MOVE_FORW ,0)-getkey(MOVE_BACK,0));

        move.zy *= rot(rotcam.y);
        move.zx *= rot(rotcam.x);

        vec3 newpos = data.xyz+movespeed*move*iTimeDelta;

        data.xyz = newpos;

        return data;
    } else if (index == camrot) {

        if (refresh) {
            return vec4(0,0,0,0);
        }

        if (min(iMouse2.z,iMouse.z) > 0.5) {
            data.xy += (iMouse.xy-iMouse2.xy)/iResolution.y*sensitivity;
        }

        return data;
    } else if (index == sphere) {

        if (refresh) {
            return vec4(0,0,0,0);
        }

        if (getkey(KEY_SPACE,0) > 0.5) {
            data = getdata(campos);
        }

        return data;
    }


    return data;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    fragColor = updatedata(coord.x+coord.y*int(iResolution.x));
}

