// Tweaked values
const float n = 0.0;
const float m = 0.15;
const float p = 0.065;
const float f = 0.222;
const float q = 0.163;

float pwc_a(float x)
{
    return p * pow((x-n)/(m-n),(q-p)*(m-n)/(p*(f-m)));
}
float pwc_b(float x)
{
    return (q-p)/(f-m)*x-m*(q-p)/(f-m)+p;
}
float pwc_c(float x)
{
    return 2.0/pi*(1.0-q)*atan(pi/2.0*(q-p)/(1.0-q)*(x-f)/(f-m))+q;
}
float pwc(float x)
{
    if(x < n)
        return 0.0f;
    else if(x < m)
        return pwc_a(x);
    else if(x < f)
        return pwc_b(x);
    else
        return pwc_c(x);
}
vec3 pwc_tonemap(vec3 c)
{
    c = m1 * c;
    vec3 tmp = vec3(pwc(c.r), pwc(c.g), pwc(c.b));

    c = m2 * tmp;

    return pow(clamp(c, 0.0, 1.0), vec3(1.0 / 2.2));
}

