mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdGyroid(vec3 p, float scale, float thickness, float bias) {
	p *= scale;
    return abs(dot(sin(p), cos(p.zxy))+bias)/scale - thickness;
}

float GetDist(vec3 p) {
    p.xy*=Rot(iTime*0.11);
    p.zx*=Rot(iTime*0.11);
    float box = sdBox(p, vec3(1.25));

    // return box;
    float gyro = sdGyroid(p,abs(5.0*tanh(iTime*3.65)),0.125,2.0)/1.0;
    return max(box, gyro*2.);
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    //uv.x = abs(uv.x);
    uv.y = abs(uv.y);
	vec2 m = vec2(cos(iTime*5.),sin(iTime*5.))/iResolution.xy;

    vec3 col = vec3(0);
    float amp = texture(iChannel0, vec2(10., 0.0)).r*1.;
    vec3 ro = vec3(sin((iTime)*0.06), 3., -cos((iTime+ amp)*0.36));
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);

    	float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
    	col += dif;  
    }

    col = pow(col, vec3(0.2 + abs(2. * cos(iTime*0.01))));	// gamma correction

    fragColor = vec4(hsv2rgb(vec3(-0.40+col.x*0.55,0.55,0.8)),1.0);
}

