// Fast biquadratic (2D) and triquadratic (3D) texture
// filtering, with 4 samples and 8 samples respectively.
// Based on an example by Roger Cabo Ashauer until I
// realized it's equivalent to Paniq's biquadratic
// implementation https://www.shadertoy.com/view/wtXXDl
//
// Top:     2D biquadratic  filtering
// Bottom:  3D triquadratic filtering


// Other texture interpolation shaders:
// 
// Samples      Type
// ----------   ---------
//  1 linear    2x2 cubic     : https://www.shadertoy.com/view/XsfGDn
//  4 linear    3x3 quadratic : https://www.shadertoy.com/view/WtyXRy and https://www.shadertoy.com/view/wtXXDl
//  4 linear    4x4 cubic     : https://www.shadertoy.com/view/4df3Dn and https://www.shadertoy.com/view/tlXSR2
//  9 linear    4x4 cubic     : https://www.shadertoy.com/view/MtVGWz
// 16 nearest   4x4 cubic     : https://www.shadertoy.com/view/XsSXDy

// Enable this to see continuity properties
// #define SHOW_DERIVATIVES



vec4 texture2( sampler2D sam, vec2 uv );
vec4 texture3( sampler3D sam, vec3 uv );

vec4 textureQuadratic( in sampler2D sam, in vec2 p )
{
    float texSize = float(textureSize(sam,0).x); 

#if 1
    //Roger/iq style
	p = p*texSize;
	vec2 i = floor(p);
	vec2 f = fract(p);
	p = i + f*0.5;
	p = p/texSize;
    //f = f*f*(3.0-2.0*f); // optional for extra sweet
	float w = 0.5/texSize;
	return mix(mix(texture2(sam,p+vec2(0,0)),
                   texture2(sam,p+vec2(w,0)),f.x),
               mix(texture2(sam,p+vec2(0,w)),
                   texture2(sam,p+vec2(w,w)),f.x), f.y);

#else
    // paniq style (https://www.shadertoy.com/view/wtXXDl)
    vec2 f = fract(p*texSize);
    vec2 c = (f*(f-1.0)+0.5) / texSize;
    vec2 w0 = p - c;
    vec2 w1 = p + c;
    return (texture(sam, vec2(w0.x, w0.y))+
    	    texture(sam, vec2(w0.x, w1.y))+
    	    texture(sam, vec2(w1.x, w1.y))+
    	    texture(sam, vec2(w1.x, w0.y)))/4.0;
#endif    


}

vec4 textureQuadratic( in sampler3D sam, in vec3 p )
{
    float texSize = float(textureSize(sam,0).x); 

	p = p*texSize;
	vec3 i = floor(p);
	vec3 f = fract(p);
	p = i + f*0.5;
	p = p/texSize;

	float w = 0.5/texSize;

	return mix(mix(mix(texture3(sam,p+vec3(0,0,0)),
                       texture3(sam,p+vec3(w,0,0)),f.x),
                   mix(texture3(sam,p+vec3(0,w,0)),
                       texture3(sam,p+vec3(w,w,0)),f.x), f.y),
               mix(mix(texture3(sam,p+vec3(0,0,w)),
                       texture3(sam,p+vec3(w,0,w)),f.x),
                   mix(texture3(sam,p+vec3(0,w,w)),
                       texture3(sam,p+vec3(w,w,w)),f.x), f.y), f.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.y;
    vec2 p2 = p*0.1;
    vec3 p3 = vec3(p*0.2,0.171);

	vec3 colA = (p.y<0.5)?texture2( iChannel0, p2 ).xyz:
                          texture3( iChannel1, p3 ).xyz;
    vec3 colB = (p.y<0.5)?textureQuadratic( iChannel0, p2 ).xyz:
                          textureQuadratic( iChannel1, p3 ).xyz;

	float g = sin(3.1416*p.x + iTime);

	vec3 col = mix( colA, colB, smoothstep( -0.1, 0.1, g ) );

    #ifdef SHOW_DERIVATIVES
    col = 0.5+0.1*vec3(dFdx(col.x),dFdy(col.x),0.0)*iResolution.y;
    #endif

	col *= smoothstep( 0.0, 0.01, abs(g-0.0) );
	col *= smoothstep( 0.0, 0.01, abs(p.y-0.5) );

    fragColor = vec4( col, 1.0 );
}

