vec4 texture2( sampler2D sam, vec2 uv )
{
#ifndef SHOW_DERIVATIVES    
    return texture(sam,uv);
#else    
    float res = float(textureSize(sam,0).x);
    uv = uv*res - 0.5;
    vec2 iuv = floor(uv);
    vec2 f = fract(uv);
	vec4 rg1 = textureLod( sam, (iuv+ vec2(0.5,0.5))/res, 0.0 );
	vec4 rg2 = textureLod( sam, (iuv+ vec2(1.5,0.5))/res, 0.0 );
	vec4 rg3 = textureLod( sam, (iuv+ vec2(0.5,1.5))/res, 0.0 );
	vec4 rg4 = textureLod( sam, (iuv+ vec2(1.5,1.5))/res, 0.0 );
	return mix( mix(rg1,rg2,f.x), mix(rg3,rg4,f.x), f.y );
#endif    
}

vec4 texture3( sampler3D sam, vec3 uv )
{
#ifndef SHOW_DERIVATIVES    
    return texture(sam,uv);
#else    
    float res = float(textureSize(sam,0).x);
    uv = uv*res - 0.5;
    vec3 iuv = floor(uv);
    vec3 f = fract(uv);
	vec4 rg1 = textureLod( sam, (iuv+ vec3(0.5,0.5,0.5))/res, 0.0 );
	vec4 rg2 = textureLod( sam, (iuv+ vec3(1.5,0.5,0.5))/res, 0.0 );
	vec4 rg3 = textureLod( sam, (iuv+ vec3(0.5,1.5,0.5))/res, 0.0 );
	vec4 rg4 = textureLod( sam, (iuv+ vec3(1.5,1.5,0.5))/res, 0.0 );
	vec4 rg5 = textureLod( sam, (iuv+ vec3(0.5,0.5,1.5))/res, 0.0 );
	vec4 rg6 = textureLod( sam, (iuv+ vec3(1.5,0.5,1.5))/res, 0.0 );
	vec4 rg7 = textureLod( sam, (iuv+ vec3(0.5,1.5,1.5))/res, 0.0 );
	vec4 rg8 = textureLod( sam, (iuv+ vec3(1.5,1.5,1.5))/res, 0.0 );
	return mix(mix( mix(rg1,rg2,f.x), mix(rg3,rg4,f.x), f.y ),
               mix( mix(rg1,rg2,f.x), mix(rg3,rg4,f.x), f.y ), f.z );
#endif    
}

float TK = 1.;
float PI = 3.1415926535;

vec2 rot(vec2 p,float r){
	mat2 m = mat2(cos(r),sin(r),-sin(r),cos(r));
	return m*p;
}

vec2 pmod(vec2 p,float n){
	float np = 2.0*PI/n;
	float r = atan(p.x,p.y)-0.5*np;
	r = mod(r,np)-0.5*np;
	return length(p)*vec2(cos(r),sin(r));
}

float cube(vec3 p,vec3 s){
	vec3 q = abs(p);
	vec3 m = max(s-q,0.0);
	return length(max(q-s,0.0))-min(min(m.x,m.y),m.z);
}

float dist(vec3 p){
	p.z -= 1.*TK*iTime;
	p.xy = rot(p.xy,1.0*p.z);
	p.xy = pmod(p.xy,6.0);
	float k = 0.7;
	float zid = floor(p.z*k);
	p = mod(p,k)-0.5*k;
	for(int i = 0;i<4;i++){
		p = abs(p)-0.3;

		p.xy = rot(p.xy,1.0+zid+0.1*TK*iTime);
		p.xz = rot(p.xz,1.0+4.7*zid+0.3*TK*iTime);
	}
	return min(cube(p,vec3(0.3)),length(p)-0.4);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv = 2.0*(uv-0.5);
	uv.y *= iResolution.y/iResolution.x;
	uv = rot(uv,TK*iTime);
	vec3 ro = vec3(0.0,0.0,0.1);
	vec3 rd = normalize(vec3(uv,0.0)-ro);
	float t  =2.0;
	float d = 0.0;
	float ac = 0.0;
	for(int i = 0;i<66;i++){
		d = dist(ro+rd*t)*0.2;
		d = max(0.0000,abs(d));
		t += d;
		if(d<0.001)ac += 0.1;//exp(-15.0*d);
	}
	vec3 col = vec3(0.0);
	col = vec3(0.1,0.7,0.7)*0.2*vec3(ac);//vec3(exp(-1.0*t));
	vec3 pn = ro+rd*t;
	float kn = 0.5;
	pn.z += -1.5*iTime*TK;
	pn.z = mod(pn.z,kn)-0.5*kn;
	float em = clamp(0.01/pn.z,0.0,100.0);
	col += 3.0*em*vec3(0.1,1.0,0.1);
	col = clamp(col,0.0,1.0);
	//col = 1.0-col;


    // Output to screen
    fragColor = vec4(col,0.);
}

#define PI      3.14159265
#define TWO_PI  6.28318530
#define HALF_PI 1.57079633

vec3 circle(vec2 uv, float rad, float i){
    float d  = length(uv);
    float a  = atan(uv.y, uv.x);
    vec3 c = vec3(1.);
    vec3 colmult;

    if(i == 0.) colmult = vec3(1., 0., 0.);
    if(i == 1.) colmult = vec3(0., 1., 0.);
	if(i == 2.) colmult = vec3(0., 0., 1.);


    rad += 0.05*cos(5.*a - i*HALF_PI + 3.*iTime)*pow(.5+.5*cos(iTime - a), 6.);
    c -= 1.-smoothstep(rad, rad+0.01, d) + smoothstep(rad*.93, rad*.93+0.01, d);

    return c * colmult;
}


