float Diamond (vec2 uv)

{
    uv *=  rotate2d(PI/2.);

    uv *= mat2(-1.,.6, 1.,.6); //Scale

    float mask = Rect(uv, -.03, .03, -.03, .03, .001);

    return mask;  
}

float DiamondRing(vec2 uv)
{
    uv *=  rotate2d(PI/16.);

    float mask = 0.;
    for (float i = 0.; i <= 2.* PI; i+= PI/8.) // thick 8 lines
    {
       uv *=  rotate2d(i);

       uv += vec2(0., .395);

       mask += Diamond(uv); 

       uv -= vec2(0., .395);
    }
    return mask; 
}

float Triangle(vec2 uv)
{
    //uv *=  rotate2d(PI/4.);

    //uv *= mat2(-1.,.6, 1.,.6); //Scale

    uv *= 2.;

    float mask = Tri(uv, .001);

    return mask;  
}

float TriRing(vec2 uv)
{
    uv *=  rotate2d(PI/16.);

    float mask = 0.;
    for (float i = 0.; i <= 2.* PI; i+= PI/8.) // thick 8 lines
    {
       uv *=  rotate2d(i);

       uv += vec2(0., .295);

       mask += Triangle(uv); 

       uv -= vec2(0., .295);
    }
    return mask; 
}





vec4 CenterCircle(vec2 uv)
{
