    float brownLine3 = S(.15, .148, d);
    col.rgb = mix(col.rgb, vec3(0.31, 0.196, 0.14), brownLine3); 



    float greenRing = S(.148, .146, d);
    col.rgb = mix(col.rgb, vec3(.678, .910, .931), greenRing);      



    float purpleRing = S(.136, .135, d);
   	col.rgb = mix(col.rgb, vec3(.757, .773, 925), purpleRing);    


    float blueCenter = S(.126, .125, d);
    col.rgb = mix(col.rgb, vec3(0.8, 0.894, 0.996), blueCenter);  





    return col;

}

vec4 Base(vec2 uv)
{

	vec4 col = vec4(0.937, 0.937, 0.812, 1.);

    //d = distance
    float d = length(uv);

    col.a = S(.5, .49, d);


    float edgeShade = remap01(.45, .5, d);

    edgeShade *= edgeShade;
    col.rgb *= 1. - edgeShade * .5;



    col.rgb = mix(col.rgb, vec3(0.937, 0.937, 0.812), S(.475, .485, d)); //edge shadow

    col.rgb = mix(col.rgb, vec3(0.31, 0.196, 0.14), S(.48, .49, d));   //Brown outline


    float purpleOutter = S(.445, .435, d); //purple outter ring

    col.rgb = mix(col.rgb, vec3(.757, .773, 925), purpleOutter);


    float green = S(.43, .42, d);//green ring
    col.rgb = mix(col.rgb, vec3(.678, .910, .931), green);


    float purpleInner = S(.37, .36, d); //purple inner ring

    col.rgb = mix(col.rgb, vec3(.757, .773, 925), purpleInner);

    float yellowCenter1 = S(.355, .345, d);
    col.rgb = mix(col.rgb, vec3(0.925, 0.953, 0.957), yellowCenter1);


    float brownLine1 = S(.28, .279, d);
    col.rgb = mix(col.rgb, vec3(0.31, 0.196, 0.14), brownLine1);

    float yellowCenter2 = S(.279, .277, d);
