    float brownLine2 = S(.205, .203, d);
    col.rgb = mix(col.rgb, vec3(0.31, 0.196, 0.14), brownLine2);


    float yellowCenter3 = S(.203, .202, d);
    col.rgb = mix(col.rgb, vec3(0.925, 0.953, 0.957), yellowCenter3);   


    float brownLine3 = S(.15, .148, d);
    col.rgb = mix(col.rgb, vec3(0.31, 0.196, 0.14), brownLine3);    


