    return col;
}



float Thick8LinesMask(vec2 uv)
{
    float mask = 0.;

    for (float i = .0; i <= 2.* PI; i+= PI/4.) // thick 8 lines
    {
       uv *=  rotate2d(i);
       mask = max(mask, Rect(uv, -.005, .005, -.49, .49, .001));
    }
    return mask;
}

float LightLinesMask(vec2 uv)
{
    float mask = 0.;

    for (float i = .0; i <= 2.* PI; i+= PI/32.) // thick 8 lines
    {
       uv *=  rotate2d(i);
       mask = max(mask, Rect(uv, -.001, .001, -.49, .49, .001));
    }
    return mask;
}

vec4 Rotunda(vec2 uv)
{
    vec4 lineColor = vec4(0.31, 0.196, 0.14, 1.);
    vec4 diamondColor = vec4(0.906, 0.451, 0.529, 1.);
    vec4 triColor = vec4(.678, .910, .931, 1.);

	vec4 col = vec4(0.5, 0.5, 0.5, 1.);

    vec4 base = Base(uv);

    float thicklines = Thick8LinesMask(uv);

    float lightlines = LightLinesMask(uv);

    float diamondRing = DiamondRing(uv);

    float triRing = TriRing(uv);

    vec4 center = CenterCircle(uv);


    col = mix(col, base, base.a);

    col = mix(col, lineColor, thicklines);

    col = mix(col, center, center.a);

    col = mix(col, triColor, triRing);   

    col = mix(col, diamondColor, diamondRing);

    //col = mix(col, lineColor, lightlines);


    return col;

}



