float func(float x, float y){

	x=abs(mod(x,2.)-1.);

	return (((-2.*y)*x)+(3.*y))*x*x;
}

float func_derv(float x, float y){

	x=abs(mod(x,2.)-1.);

	return 6.*y*x*(1.-x);
}

float wallpaper_dis(vec2 uv, float wavelen){
	uv.y=abs(mod(uv.y,wavelen)-wavelen/2.);

	float dis=1e38;

	for(float i=0.;i<4.;i+=1.){

		float x=uv.x*2./wavelen;
		float y=wavelen/2.*(.65+i*.05);

		vec2 grad=vec2(2./wavelen*func_derv(x,y),1);

		float cur_dis=abs(uv.y-func(x,y))/length(grad);

		dis=min(cur_dis,dis);
	}

	return dis;
}

