        dis=min(dis,distance(disk_center3,uv)-disk_radius);
        color=mix(mix(color,disk_col,disk_alpha3),color,smoothstep(-border/2.,border/2.,dis));
        dis=1e38;
    }

    if(t0<rgb_logo_time || !rgb_flicker(t0-rgb_logo_time)){
    	dis=min(dis,c1_dis(uv-disk_center1));
        color=mix(mix(color,font_col,disk_alpha1),color,smoothstep(-border/2.,border/2.,dis));
        dis=1e38;
        dis=min(dis,c2_dis(uv-disk_center2));
        color=mix(mix(color,font_col,disk_alpha2),color,smoothstep(-border/2.,border/2.,dis));
        dis=1e38;
        dis=min(dis,f_dis(uv-disk_center3));
        color=mix(mix(color,font_col,disk_alpha3),color,smoothstep(-border/2.,border/2.,dis));
    }
    else{
        const vec3[4] colors=vec3[4](blue,green,red,black);

        for(int i=ZERO;i<4;i++){
            dis=min(dis,c1_dis(uv+float(-3+2*i)*logo_offset));
            dis=min(dis,c2_dis(uv+float(-3+2*i)*logo_offset));
            dis=min(dis,f_dis(uv+float(-3+2*i)*logo_offset));
            color=mix(colors[i],color,smoothstep(-border/2.,border/2.,dis));
            dis=1e38;
        }
    }

	fragColor=vec4(color,1);
}

