#define ZERO min(0,iFrame)

float filtered_triangle(float x, float fac){
    float val=0.;

    x*=4.;

    float old_x=mod(x,4.);

    x=mod(x,2.);

    if(x>=1.){
        x=2.-x;
    }

    if(x<fac){
        val=x;
    }
    else if(x<=1.){
        float tmp=1.-fac;
        val=fac+sin(mod((x-fac)/tmp*.5*pi,2.*pi))*tmp*2./pi;
    }

    if(old_x>2.){
        val*=-1.;
    }

    return val;
}

//modified from https://www.shadertoy.com/view/llGSzw
float hash(float t)
{
    uint n=uint(t*iSampleRate);
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}

vec4 hash4(float t)
{
    uint n=uint(t*iSampleRate);
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec4 k = n * uvec4(n,n*16807U,n*48271U,n*72037U);
    return vec4( k & uvec4(0x7fffffffU))/float(0x7fffffff);
}

float exp_decay(float t, float decay_time){
    return exp(log(eps)*t/decay_time);
}

float linear_decay(float t, float duration){
    return max(0.,1.-max(0.,t)/duration);
}

float linear_attack(float t, float duration){
    return clamp(t/duration,0.,1.);
}

//attack, decay, sustain, increase, release
float linear_adsir(float t, float att_amp, float att_time, float dec_time, float sus_amp, float sus_time, float inc_amp, float inc_time, float rel_time){
    float amp=0.;

    t=max(0.,t);

    if(t<att_time){
        amp=att_amp*linear_attack(t,att_time);
    }
    else if(t<att_time+dec_time){
        amp=mix(att_amp,sus_amp,linear_attack(t-att_time,dec_time));
    }
    else if(t<att_time+dec_time+sus_time){
        amp=sus_amp;
    }
    else if(t<att_time+dec_time+sus_time+inc_time){
        amp=mix(sus_amp,inc_amp,linear_attack(t-(att_time+dec_time+sus_time),inc_time));
    }
    else if(t<att_time+dec_time+sus_time+inc_time+rel_time){
        amp=mix(inc_amp,0.,linear_attack(t-(att_time+dec_time+sus_time+inc_time),rel_time));
    }

    return amp;
}

float fizzle(float t, float dur){
    float amp=.0;
    if(t>0. && t<dur){
        amp=.1;
    }

    return amp*sin(10.*t*2.*pi)*(.2+max(0.,.8*sin(100.*t*2.*pi)))*(-1.+(hash(t)+hash(t+.001)));
}

float drum(float t, float freq, float beat_t, float speed){
    //beat_t is the time of the beat
    //speed is for changing sound according to current rythm speed

    float cur_drum_time=drum_time/pow(speed,drum_time_pow);

    float amp_decay_time=drum_exp_fac*drum_time;
    float lin_amp_decay_time=drum_lin_fac*drum_time;
    float freq_decay_time=drum_freq_fac*cur_drum_time;
    float filter_decay_time=drum_filter_fac*cur_drum_time;

    float kick_freq=drum_kick_fac*freq;
    float bass_freq=drum_bass_fac*freq;

    t=max(0.,t-beat_t);

    float tmp=log(eps)/freq_decay_time;
    float phase=(exp(tmp*t)-1.)/tmp*(kick_freq-bass_freq)+bass_freq*t;

    float amp=drum_amp*exp_decay(t,amp_decay_time)*linear_decay(t,lin_amp_decay_time);

    float filter_val=exp_decay(t,filter_decay_time);

    return amp*filtered_triangle(phase,filter_val);
}

float sine_tone(float t, float freq, float dur){

    float amp=1.;

    float param=linear_adsir(t,attack_param,attack_time*dur,decay_time*dur,sustain_param,sustain_time*dur,increase_param,increase_time*dur,release_time*dur);
    amp*=linear_adsir(t,attack_amp,attack_time*dur,decay_time*dur,sustain_amp,sustain_time*dur,increase_amp,increase_time*dur,release_time*dur);
    amp*=1.+vibrato_depth*sin(mod(max(t-dur*(attack_time+decay_time+sustain_time),0.)*vibrato_freq*2.*pi,2.*pi));

    return amp*filtered_triangle(t*freq,param);
}

float fade(float t, float duration){
    float val=0.;

    if(t>0. && t<duration){
        val=1.;
    }

    return val;
}

bool rgb_flicker(float t){
	bool show=false;

    if((t>flicker1 && t<flicker1+flicker_dur) || (t>flicker2 && t<flicker2+flicker_dur) || t>flicker3){
        show=true;
    }

    return show;
}

float disk_animation(float t){
    return max(1.-1./animation_dur*max(t,0.),0.);
