vec2 last_beat(float t){
    t=max(0.,t);
    float t0=t;

    t*=time_fac;

    float second=floor(t);

    float speed=min(second+1.,float(max_speed))*float(speed_fac);

    float frac=fract(t);

    vec2 ret=vec2(0);

    //point in time of last beat
    ret.x=(second+floor(frac*3.*speed)/(3.*speed))/time_fac;
    //"position" of last beat
    ret.y=mod(floor(frac*3.*speed),3.);

    return ret;
}

vec2 drum_pattern2(float t){
    vec2 val=vec2(0);

    t=max(0.,t);

    float t0=t;

    t*=time_fac;

    float second=floor(t);

    float speed=min(second+1.,float(max_speed))*float(speed_fac);
    float frac=fract(t);

    int n=int(ceil(drum_time*time_fac*3.*speed));
    int ind0=int(frac*3.*speed);
    int ind1=max(0,ind0-n);
    for(int ind=ind0;ind>=ind1;ind--){
        float beat=(second+float(ind)/(3.*speed))/time_fac;
        int type=int(mod(float(ind),3.));

        vec2 drum_pan;

        if(type == 0){
            drum_pan=drum1_pan;
        }
        else if(type == 1){
            drum_pan=drum2_pan;
        }
        else if(type == 2){
            drum_pan=drum3_pan;
        }

        drum_pan=mix(vec2(1.),drum_pan,disk_animation(beat));

        val+=drum(t0,freqs[type],beat,speed)*drum_pan;
    }

    second-=1.;
    speed=min(second+1.,float(max_speed))*float(speed_fac);
    frac=t-second;
    //n=int(ceil(drum_time*time_fac*3.*speed));
    n=4;
    ind0=int(3.*speed-1.);
    ind1=max(0,ind0-n);

    for(int ind=ind0;ind>=ind1;ind--){
        float beat=(second+float(ind)/(3.*speed))/time_fac;
        int type=int(mod(float(ind),3.));

        vec2 drum_pan;

        if(type == 0){
            drum_pan=drum1_pan;
        }
        else if(type == 1){
            drum_pan=drum2_pan;
        }
        else if(type == 2){
            drum_pan=drum3_pan;
        }

        drum_pan=mix(vec2(1.),drum_pan,disk_animation(beat));

        val+=drum(t0,freqs[type],beat,speed)*drum_pan;
    }

    float val2=0.;

    float fade_param=linear_attack(t0-begin,fade_time);

    for(int i=1;i<3;i++){
        for(int j=0;j<3;j++){
            val2+=sine_amp*sine_tone(t0-(begin+offset_width*hash(92.81+float(i*3+j))),(1.+detune_width*hash(float(i*3+j)))*float(1<<i)*freqs[j],dur);
        }
    }

    val=mix(val,vec2(0),fade_param);

    float val3=.0;

    val3+=fizzle(t0-(rgb_logo_time+flicker1),fizzle_dur1);
    val3+=fizzle(t0-(rgb_logo_time+flicker2),fizzle_dur2);
    val3+=fizzle(t0-(rgb_logo_time+flicker3),fizzle_dur3);

    return val+val2+val3;
}

vec3 fade_pattern2(float t){
    vec3 val=vec3(0);

    vec2 lb=last_beat(t);

    val[int(lb.y)]=1.;

    if(t>animation_dur){
        val=vec3(1);
    }

    return val;
}

float wave_pattern2(float t, vec2 uv){
    float dis=1e38;

    t=max(0.,t);

    float t0=t;

    t*=time_fac;

    float second=floor(t)-1.;

    float speed=min(second+1.,float(max_speed))*float(speed_fac);
    float frac=t-second;

    int ind0=int(3.*speed-1.);
    int ind1=0;

    vec2 wave_center=vec2(0);

    for(int ind=ind1;ind<=ind0;ind++){
        float beat=(second+float(ind)/(3.*speed))/time_fac;
        if(beat < begin+dur*(attack_time+decay_time+sustain_time+increase_time)){
            int type=int(mod(float(ind),3.));

            float t2=disk_animation(beat);

            if(type==0){
                wave_center=original_disk_center1*t2;
            }
            else if(type==1){
                wave_center=original_disk_center2*t2;
            }
            else if(type==2){
                wave_center=original_disk_center3*t2;
            }

            dis=beat_wave(t0,beat,uv,wave_center,dis);
        }
    }

    second+=1.;
    speed=min(second+1.,float(max_speed))*float(speed_fac);
    frac=t-second;
    ind0=int(frac*3.*speed);
    ind1=0;

    for(int ind=ind1;ind<=ind0;ind++){
        float beat=(second+float(ind)/(3.*speed))/time_fac;
        if(beat < begin+dur*(attack_time+decay_time+sustain_time+increase_time)){
            int type=int(mod(float(ind),3.));

            float t2=disk_animation(beat);

            if(type==0){
                wave_center=original_disk_center1*t2;
            }
            else if(type==1){
                wave_center=original_disk_center2*t2;
            }
            else if(type==2){
                wave_center=original_disk_center3*t2;
            }

            dis=beat_wave(t0,beat,uv,wave_center,dis);
        }
    }

    return dis;
}

vec2 drum_pattern(float t){
    t-=pattern_start_time;

    if(t<pattern2_start_time){
        return drum_pattern1(t);
    }
    else{
        return drum_pattern2(t-pattern2_start_time);
    }
}

vec3 fade_pattern(float t){
    t-=pattern_start_time+audio_offset;

    if(t<pattern2_start_time){
        return fade_pattern1(t);
    }
    else{
        return fade_pattern2(t-pattern2_start_time);
    }
}

float wave_pattern(float t, vec2 uv){
    t-=pattern_start_time+audio_offset;

    if(t<pattern2_start_time){
        return wave_pattern1(t,uv);
    }
    else{
        return wave_pattern2(t-pattern2_start_time,uv);
    }
}

