vec3 sphere = vec3(0, 0, 2);
float sphere_size = 1.3;

const float INFINITY = 100000.0;

#define ENV_MAP // enable envmap
//#define REFLETIVITY_METAL_APPROX // replace dieletric Fresnel with just 1.0    

//
// Wavelength dependent IOR and Extinction derived from
// https://refractiveindex.info/
// 
// Wavelength micrometers used are r = 0.63, g = 0.532, b = 0.465
// 

void getNK( out vec3 n, out vec3 k ) { 

	float stage = mod( iTime / ( 3.14156 * 2.0 ), 6.0 );

	if( stage < 1.0 ) {
		// IRON
		n = vec3(2.943, 2.9211, 2.4728);
		k = vec3(2.9718, 2.9236, 2.7067);	
    }
	else if( stage < 2.0 ) {
    	// SILVER
        // NOTE: almost no difference between Dielectric and Conductive Fresnel
        n = vec3(0.056909, 0.054007, 0.046878);
        k = vec3(4.2543, 3.429, 2.8028);	
    }
	else if( stage < 3.0 ) {
        // COPPER
        n = vec3(0.27527,1.1159,1.2469);
        k = vec3(3.3726, 2.5956, 2.4562);
    }
	else if( stage < 4.0 ) {
		// ALUMINIUM
		n = vec3(1.1167, 0.97274, 0.57037);
		k = vec3(6.8768, 6.5119, 5.2086);	
    }
	else if( stage < 5.0 ) {
		// NICKEL
        n = vec3(1.9479, 1.8975, 1.7094);
        k = vec3(3.7424, 3.5489, 2.8128);	
    }
	else if( stage < 6.0 ) {
        // GOLD
        // NOTE: almost no difference between Dielectric and Conductive Fresnel
        n = vec3(0.18836, 0.54386, 1.3319);
        k = vec3(3.4034, 2.2309, 1.8693);
    }
}

float pow2( const in float a ) { return a *a; }
vec3 pow2( const in vec3 a ) { return a *a; }

//
// Approximation of Spectral Complex Fresnel based on just sampling RGB wavelengths
//
// source: https://docs.chaosgroup.com/display/OSLShaders/Complex+Fresnel+shader
//
vec3 F_ConductiveFresnel( const in vec3 n, const in vec3 k, const in float dotNV ) {

    vec3 rs_num = pow2( n ) + pow2( k ) - 2.0 * n * dotNV + pow2( dotNV );
    vec3 rs_den = pow2( n ) + pow2( k ) + 2.0 * n * dotNV + pow2( dotNV );
    vec3 rs = rs_num / rs_den;

    vec3 rp_num = ( pow2( n ) + pow2( k ) ) * pow2( dotNV ) - 2.0 * n * dotNV + 1.0;
    vec3 rp_den = ( pow2( n ) + pow2( k ) ) * pow2( dotNV ) + 2.0 * n * dotNV + 1.0;
    vec3 rp = rp_num / rp_den;

    return clamp( 0.5 * ( rs + rp ), vec3(0.0), vec3(1.0) );
}

vec3 F_DielectricFresnel( const in vec3 reflectivity, const in float dotNV ) {
	// Based on the standard Schlick approximation
	float fresnel = pow( 1.0 - dotNV, 5.0 );
	return vec3( ( 1.0 - reflectivity ) * fresnel + reflectivity );
}

float raySphere(vec3 rpos, vec3 rdir, vec3 sp, float radius, inout vec3 point, inout vec3 normal) {
	radius = radius * radius;
    vec3 tmp = rpos - sp;
	float dt = dot(rdir, -tmp);
	if (dt < 0.0)
		return INFINITY;
	tmp.x = dot(tmp, tmp);
	tmp.x = tmp.x - dt*dt;
	if (tmp.x >= radius)
		return INFINITY;
	dt = dt - sqrt(radius - tmp.x);
	point = rpos + rdir * dt;
	normal = normalize(point - sp);
	return dt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;
	uv.y = -uv.y;
	vec3 ray = vec3(uv.x, uv.y, 1.0);
	ray = normalize(ray);	

	vec3 point, normal; 
	float dist = raySphere(vec3(0.0), ray, sphere, sphere_size, point, normal);

    vec3 viewDir = normalize( -point );
    float dotVN = dot( viewDir, normal );

    vec3 n, k;
    getNK( n, k );

    // Calculate reflectivity (F0) from the conductive Fresnel
    vec3 reflectivity = F_ConductiveFresnel( n, k, 1.0 );

    vec3 reflection = reflect( viewDir, normal );

    vec3 env = texture( iChannel2, reflection ).xyz;

    vec3 F = vec3( 0.0 );

	if (dist < INFINITY) {

        if( ( uv.y + cos( iTime ) ) < 0.0 ) {  
#ifdef REFLETIVITY_METAL_APPROX        
			F = reflectivity;
#else
            F = F_DielectricFresnel( reflectivity, dotVN );
#endif
        }
        else {
			F = F_ConductiveFresnel( n, k, dotVN );
        }
    }


	fragColor.xyz = F;
#ifdef ENV_MAP
    fragColor.xyz *= env * 1.5;
#endif

    if (dist >= INFINITY) {

        if( ( uv.y + cos( iTime ) ) > 0.0 && abs( uv.x ) > 1.0 ) {  
            fragColor.xyz = vec3( 0.2 );
	    }

    }

	fragColor.w = 1.0;
}

