float pow2( const in float a ) { return a *a; }
vec3 pow2( const in vec3 a ) { return a *a; }

void edgeTintToConductiveFresnel( const in vec3 reflectivity, const in vec3 edgeTint, out vec3 n, out vec3 k ) {
	// this is based on the code in Autodesk's Standard Surface OSL as well as in the OSL repository in my thread from 2015.
	vec3 r = clamp( reflectivity, vec3(0), vec3(0.99) );
	vec3 g = edgeTint;
	vec3 r_sqrt = sqrt( r );
	vec3 n_min = ( 1.0 - r ) / ( 1.0 + r );
	vec3 n_max = ( 1.0 + r_sqrt ) / ( 1.0 - r_sqrt );
	n = mix( n_max, n_min, g );
	vec3 k2 = (( n + 1.0 ) * ( n + 1.0 ) * r - ( n - 1.0 ) * ( n - 1.0 ) ) / ( 1.0 - r );
	k2 = max( k2, 0.0 );
	k = sqrt( k2 );
}

//
// Approximation of Spectral Complex Fresnel based on just sampling RGB wavelengths
//
// Refractive index for red, green, blue wavelengths (f.e. for 0.65, 0.55, 0.45 micrometers)
// Extinction coefficient for red, green, blue wavelengths (f.e. for 0.65, 0.55, 0.45 micrometers)
//
// source: https://docs.chaosgroup.com/display/OSLShaders/Complex+Fresnel+shader
//
vec3 F_ConductiveFresnel( const in vec3 reflectivity, const in vec3 edgeTint, const in float dotNV ) {

	vec3 n, k;
	edgeTintToConductiveFresnel( reflectivity, edgeTint, n, k );

	//n=vec3(0.27105, 0.67693, 1.3164);
	//k=vec3(3.6092, 2.6247, 2.2921);

    vec3 rs_num = pow2( n ) + pow2( k ) - 2.0 * n * dotNV + pow2( dotNV );
    vec3 rs_den = pow2( n ) + pow2( k ) + 2.0 * n * dotNV + pow2( dotNV );
    vec3 rs = rs_num / rs_den;

    vec3 rp_num = ( pow2( n ) + pow2( k ) ) * pow2( dotNV ) - 2.0 * n * dotNV + 1.0;
    vec3 rp_den = ( pow2( n ) + pow2( k ) ) * pow2( dotNV ) + 2.0 * n * dotNV + 1.0;
    vec3 rp = rp_num / rp_den;

    return clamp( 0.5 * ( rs + rp ), vec3(0.0), vec3(1.0) );
}

vec3 F_DielectricFresnel( const in vec3 reflectivity, const in float dotNV ) {
	// Based on the standard Schlick approximation
	float fresnel = pow( 1.0 - dotNV, 5.0 );
	return vec3( ( 1.0 - reflectivity ) * fresnel + reflectivity );
}

float raySphere(vec3 rpos, vec3 rdir, vec3 sp, float radius, inout vec3 point, inout vec3 normal) {
	radius = radius * radius;
    vec3 tmp = rpos - sp;
	float dt = dot(rdir, -tmp);
	if (dt < 0.0)
		return INFINITY;
	tmp.x = dot(tmp, tmp);
	tmp.x = tmp.x - dt*dt;
	if (tmp.x >= radius)
		return INFINITY;
	dt = dt - sqrt(radius - tmp.x);
	point = rpos + rdir * dt;
	normal = normalize(point - sp);
	return dt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;
	uv.y = -uv.y;
	vec3 ray = vec3(uv.x, uv.y, 1.0);
	ray = normalize(ray);	

	vec3 point, normal; 
	float dist = raySphere(vec3(0.0), ray, sphere, sphere_size, point, normal);

    vec3 viewDir = normalize( -point );
    float dotVN = dot( viewDir, normal );


    vec3 reflection = reflect( viewDir, normal );

    vec3 env = texture( iChannel2, reflection ).xyz;

    vec3 F = vec3( 0.0 );

	if (dist < INFINITY) {

     if( ( uv.y + sin( iTime ) ) < 0.0 ) {  
			F = F_DielectricFresnel( reflectivity, dotVN );
        }
        else {
			F = F_ConductiveFresnel( reflectivity, edgeTint, dotVN );
        }
    }
    else {
        fragColor = vec4(0.0);
    }



	fragColor.xyz = F;
#ifdef ENV_MAP
    fragColor.xyz *= env * 1.5;
#endif

    if (dist >= INFINITY) {

        if( ( uv.y + sin( iTime ) ) > 0.0 && abs( uv.x ) > 1.0 ) {  
            fragColor.xyz = vec3( 0.2 );
	    }
    }
}

// variant of https://shadertoy.com/view/WtyXzd
// Babylonian method: https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method
// converges exponentially fast once "close enough" to solution
// -> use exponant in IEEE float representation for a very good estimate as X0. 

#define s(X)  ( X + x/(X) ) /2.  // Babylonian series

  #define C(v) smoothstep( 1.5,0., abs( v - y )/fwidth(y) )
//#define R(v) smoothstep( 1.5,0., abs( 1e2*(v-sqrt(x)) - y )/fwidth(y) )
  #define E(v) smoothstep( 1.5,0., abs( -log2(abs( v/sqrt(x)-1.) )/log(10.) -10.*U.y )*R.y/10. )

void mainImage( out vec4 O, vec2 u )
{

    vec2  R = iResolution.xy,                       
          U = u / R;
    float N = 256.,
          x = N*U.x, y = sqrt(N)*U.y,
         // --- Good first estimate using exponant in IEEE float representation
      // X0 = intBitsToFloat( ( ( (floatBitsToInt(x) >> 23) -127 )/2 + 127 ) << 23 ),
      // X0 = intBitsToFloat( (   (floatBitsToInt(x) >> 24) +64 ) << 23 ),           // Indeed even better than above :-)
         X0 = intBitsToFloat(     (floatBitsToInt(x) & 0xff000000) / 2 +(1 << 29) ), // Simplified
      // X0 = intBitsToFloat(      floatBitsToInt(x)               / 2 +(1 << 29) ), // ( less good )
      // X0 = uintBitsToFloat(    (floatBitsToUint(x) +  0x3F800000u ) / 2u ), // Even faster, see https://www.shadertoy.com/view/wlyXRt
         X1 = s(X0),  //   iteration 1                                         //              and https://en.wikipedia.org/wiki/Fast_inverse_square_root
         X2 = s(X1),  //   iteration 2                                         // invSqrt with similar cost: good for normalizations
         X3 = s(X2);  // ( iteration 3 )

    O =  C( vec4(X0,X1,X2,X3) ); 
 // O.rb += O.a;                    // if you want to display X3 as pink
    O += C( sqrt(x)  );

    O.a = 0.;
    O += E( vec4(X0,X1,X2,X3) )*.5; // -log10( relative error )
    O.rb += O.a;                    
    if (mod(u.y,R.y/10.)<1.) O += .2;// ticks per significant bit

}

// https://en.wikipedia.org/wiki/Single-precision_floating-point_format#Precision_limits_on_integer_values
// https://en.wikipedia.org/wiki/IEEE_754

void mainImage( out vec4 O, vec2 u )
{

    vec2  R = iResolution.xy,                       
          U = u / R;
    float N = 12., 
          y = exp2(N) * U.y;

    O = int(u)==int(R)/2 ? vec4(1,0,0,1)  // red separator
      : vec4(  U.x < .5 
                   ? int( log2( y ))                // left
                   : (floatBitsToInt(y) >> 23) -127 // right
            ) /N ;
}

// Author Reva - 2020-03-04

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ){
        return a + b*cos( 6.28318*(c*t+d) );
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (fragCoord - iResolution.xy*.5)/iResolution.y;

    st *= rotate2d(noise(st));

    // Cell positions
    vec2 point[10];
    vec2 move = iMouse.xy/iResolution.xy - 0.5;
    for(float i = 0.; i < 10.; i+= 1.){
        vec2 pos = random2(vec2(i) - 5.93);
        vec2 vel = vec2(noise(pos*iTime));
        point[int(i)] = clamp(pos + vel*0.5 + move,vec2(-0.8),vec2(0.8));
    }

    float m_dist = 1.;  // minimun distance

    // Iterate through the points positions
    for (int i = 0; i < 10; i++) {
        float dist = distance(st, point[i]);

        // Keep the closer distance
        m_dist = min(m_dist, dist);
    }

    // Draw the min distance (distance field)
    float t = m_dist*0.8;

    vec3 color = pal(t,vec3(0.635,0.589,0.504),vec3(0.720,0.534,0.493),vec3(0.500,0.818,0.915),vec3(0.410,0.317,0.297));

    // Show isolines
    // color -= step(0.10,m_dist)*-0.060;

    fragColor = vec4(1.0 - color,0.5 - m_dist);
}

#define PI 3.14159265359

float saturate(float x)
{
	return clamp(x, 0.0, 1.0);
}

float RemapTo01(float val, float rangeMin, float rangeMax)
{
    return saturate((val - rangeMin) / (rangeMax - rangeMin));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspectRatio = vec2(1.0, iResolution.y / iResolution.x);
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uvCentered = uv * 2.0 - vec2(1.0);
    uvCentered *= aspectRatio;

    vec2 pivot = vec2(0.05, 0.1);

    float radius = 0.5;
    vec2 diff = (uvCentered - pivot) / radius;
    float dist = length(diff);

    float mask = float(dist < 1.0);

    //diff = smoothstep(vec2(0.0), vec2(1.0), diff);

    float waveFreq = 32.0;
    float speed = 3.0;
    float distForSin = length(diff);
    distForSin = pow(distForSin, 0.5);
    float height = sin(distForSin * waveFreq - (iTime * speed));
    //height *= 0.5;
    //height = (dist < 1.0) ? heightAdd : 0.0;

    vec2 dir = normalize(diff);
    //diff += dir * height * 0.9;

    vec3 norm = normalize(vec3(
        diff.xy,
        sqrt( saturate( 1.0 - dot( diff.xy, diff.xy ) ) )
    ));

    norm.xy += dir * height * 0.9;
    norm = normalize(norm);


    //norm.z += heightAdd;
    //norm = normalize(norm);

    float softEdgeMask = RemapTo01(dist, 1.0, 0.4);
    softEdgeMask = smoothstep(0.0, 1.0, softEdgeMask);
    if (softEdgeMask > 0.0)
    {
    	norm.xy *= softEdgeMask;
    }
    norm = normalize(norm);

    vec3 col = vec3(0.5);
    col = vec3(uvCentered, 0.0);
    //col = vec3(dist);

    if (mask < 1.0)
    {
        norm = vec3(0,0,1);
    }

    col = vec3(norm);

    col = col * 0.5 + vec3(0.5);
    //col = vec3(heightAdd);


    fragColor = vec4(col,1.0);
}

