// "Knot Dynamics"  by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float Minv3 (vec3 p);
float Maxv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
mat3 DirToRMatT (vec3 vd, vec3 vu);
vec3 HsvToRgb (vec3 c);
vec4 Loadv4 (int idVar);

const int lenChain = 256;
vec3 vnObj, ltDir;
vec2 qBlk;
float dstFar, tCur, spRad;
int idBall;
const float pi = 3.14159;

#define AA  1     // optional antialiasing
#define TUBE 1    // = 0/1, show balls/tubes

#if TUBE

float CapsHit (vec3 ro, vec3 rd, float rad, float len)
{
  vec3 s;
  float dMin, d, a, b, w;
  dMin = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - rad * rad);
  if (w > 0. && a > 0.) {
    d = - b - sqrt (w);
    d /= a;
    s = ro + d * rd;
    if (d > 0. && abs (s.z) < len) {
      dMin = d;
      vnObj = vec3 (s.xy, 0.);
    } else {
      ro.z -= len * sign (s.z);
      b = dot (rd, ro);
      w = b * b - (dot (ro, ro) - rad * rad);
      if (w > 0.) {
        d = - b - sqrt (w);
        if (d > 0.) {
          dMin = d;
          vnObj = ro + d * rd;
        }
      }
    }
  }
  return dMin;
}

float PolyHit (vec3 ro, vec3 rd)
{
  mat3 rMat, rMatS;
  vec3 r, rp, vnObjS;
  float dMin, d, sz;
  sz = 0.45;
  dMin = dstFar;
  idBall = -1;
  for (int n = 0; n < lenChain + 1; n ++) {
    if (n == 0) r = Loadv4 (0).xyz;
    else {
      rp = r;
      r = (n < lenChain) ? Loadv4 (2 * n).xyz : Loadv4 (0).xyz;
      rMat = DirToRMatT (normalize (r - rp), vec3 (0., 0., 1.));
      d = CapsHit ((ro - 0.5 * (r + rp)) * rMat, rd * rMat, sz, 0.5 * length (r - rp));
      if (d < dMin) {
        dMin = d;
        rMatS = rMat;
        vnObjS = vnObj;
        idBall = n;
      }
    }
  }
  if (idBall >= 0) vnObj = rMatS * normalize (vnObjS);
  return dMin;
}

#else

float BallHit (vec3 ro, vec3 rd)
{
  vec3 v;
  float b, d, w, dMin, sz;
  dMin = dstFar;
  sz = 0.55;
  idBall = -1;
  for (int n = 0; n < lenChain; n ++) {
    v = ro - Loadv4 (2 * n).xyz;
    b = dot (rd, v);
    w = b * b + sz * sz - dot (v, v);
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        idBall = n;
      }
    }
  }
  if (idBall >= 0) vnObj = (ro - Loadv4 (2 * idBall).xyz + dMin * rd) / sz;
  return dMin;
}

#endif

vec4 SphHit (vec3 ro, vec3 rd)
{
  vec3 vn;
  float b, d, w;
  b = dot (rd, ro);
  w = b * b + spRad * spRad - dot (ro, ro);
  d = dstFar;
  if (w >= 0.) {
    d = - b - sqrt (w);
    vn = (ro + d * rd) / spRad;
  }
  return vec4 (d, vn);
}

void BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, qnBlk;
  float dn, df, bSize;
  bSize = 10. * dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    qnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, qnBlk), dot (u.yzx, qnBlk)) / bSize;
  }
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  return vec3 (0.5, 0.5, 0.7) * (0.3 + 0.1 * rd.y);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 d4;
  vec3 col, vnSph;
  float dstObj, dstExtSph, h;
  d4 = SphHit (ro, rd);
  dstExtSph = d4.x;
  col = BgCol (ro, rd);
  if (dstExtSph < dstFar) {
#if TUBE
    dstObj = PolyHit (ro, rd);
#else   
    dstObj = BallHit (ro, rd);
#endif
    if (dstObj < dstFar) {
      h = mod (float (idBall) / float (lenChain) + 0.1 * tCur, 1.);
      col = HsvToRgb (vec3 (h, 1., 0.8)) * (0.3 + 0.7 * max (dot (vnObj, ltDir), 0.)) +
         0.2 * pow (max (dot (normalize (ltDir - rd), vnObj), 0.), 16.);
    }
    vnSph = d4.yzw;
    col += 0.05 + 0.05 * max (dot (vnSph, ltDir), 0.) +
       0.15 * pow (max (dot (normalize (ltDir - rd), vnSph), 0.), 32.);
    rd = reflect (rd, vnSph);
    ro += dstExtSph * rd;
    BlkHit (ro, rd);
    qBlk = step (0.1, mod (8. * qBlk + 0.1/2., 1.));
    col = mix (col, BgCol (ro, rd) * (0.8 + 0.2 * qBlk.x * qBlk.y),
       pow (1. - abs (dot (rd, vnSph)), 3.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 rd, ro, col, wgBxC, c;
  vec2 canvas, uv, uvv;
  float asp, el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  wgBxC = vec3 (0.48 * asp, -0.46, 0.02);
  mPtr.xyz =  Loadv4 (2 * lenChain + 1).xyz;
  spRad = Loadv4 (2 * lenChain).y;
  dstFar = 20. * spRad;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0. && length (mPtr.xy * vec2 (asp, 1.) - wgBxC.xy) > wgBxC.z) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -5. * spRad);
  ltDir = vuMat * normalize (vec3 (-2., 3., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    rd = vuMat * normalize (vec3 (uvv, 4.5));
    c = ShowScene (ro, rd);
    if (abs (length (0.5 * uvv - wgBxC.xy) - wgBxC.z) * canvas.y < 2.) c = vec3 (0.5, 0.8, 0.8);
    col += c / naa;
  }
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

mat3 DirToRMatT (vec3 vd, vec3 vu)
{
  vec3 vc;
  vc = normalize (cross (vu, vd));
  return mat3 (vc, cross (vd, vc), vd);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) *
     6. - 3.) - 1., 0., 1.), c.y);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

