     knc[3] = vec3 (  23,  -23,  -4);  kns[3] = vec3 ( 55, -23, -24);
     knc[4] = vec3 ( -38,   36, -44);  kns[4] = vec3 ( -6, -10, -50);
     knc[5] = vec3 ( -14,  -13,  31);  kns[5] = vec3 ( 8,    2,  39);
