vec3 KtPoint (float a)
{
  vec3 r;
  float f;
  r = vec3 (0.);
  for (int k = 0; k < N_KNOT; k ++) {
    f = float (k + 1) * a;
    r += knc[k] * cos (f) + kns[k] * sin (f);
  }
  return sclFac * r;
}

void Step (int mId, out vec3 rm, out vec3 vm)
{
  vec3 rmN, rmN1, rmN2, dr, dr1, dr2, am;
  float fOvlap, fBend, rSep, c11, c22, c12, cd, c, dt;
  int k1, k2;
  fOvlap = 50.;
  fBend = 1000.;
  rm = Loadv4 (2 * mId).xyz;
  vm = Loadv4 (2 * mId + 1).xyz;
  am = vec3 (0.);
  for (int n = 0; n < lenChain; n ++) {
    if (n != mId) {
      rmN = Loadv4 (2 * n).xyz;
      dr = rm - rmN;
      rSep = length (dr);
      if (rSep < 1. || n == mId + 1 || n == mId - 1 ||
         mId == 0 && n == lenChain - 1 || mId == lenChain - 1 && n == 0)
         am += fOvlap * (1. - rSep) * normalize (dr);
    }
  }
  for (int m = 0; m < 4; m ++) {
    k1 = mId;
    k2 = mId;
    if (m == 0) {
      k1 -= 1;
      if (k1 < 0) k1 += lenChain;
      k2 -= 2;
      if (k2 < 0) k2 += lenChain;
    } else if (m == 1 || m == 2) {
      k1 -= 1;
      if (k1 < 0) k1 += lenChain;
      k2 += 1;
      if (k2 > lenChain - 1) k2 -= lenChain;
    } else if (m == 3) {
      k1 += 1;
      if (k1 > lenChain - 1) k1 -= lenChain;
      k2 += 2;
      if (k2 > lenChain - 1) k2 -= lenChain;
    }
    rmN1 = Loadv4 (2 * k1).xyz;
    rmN2 = Loadv4 (2 * k2).xyz;
    if (m == 0) {
      dr1 = rmN1 - rmN2;
      dr2 = rm - rmN1;
    } else if (m == 1 || m == 2) {
      dr1 = rm - rmN1;
      dr2 = rmN2 - rm;
    } else if (m == 3) {
      dr1 = rmN1 - rm;
      dr2 = rmN2 - rmN1;
    }
    c11 = dot (dr1, dr1);
    c12 = dot (dr1, dr2);
    c22 = dot (dr2, dr2);
    cd = sqrt (c11 * c22);
    c = c12 / cd - 1.;
    if (m == 0 || m == 3) c = - c;
    am += fBend * (c / cd) * ((m <= 1) ? dr1 - (c12 / c22) * dr2 : (c12 / c11) * dr1 - dr2);
  }  
  dr = rm - spRad * normalize (rm);
  rSep = length (dr);
  if (rSep < 1.) am += fOvlap * (1. - rSep) * normalize (dr);
  dt = 0.02;
  vm += dt * am;
  rm += dt * vm;
}

void Init (int mId, out vec3 rm, out vec3 vm)
{
  float mIdf;
  mIdf = float (mId);
  rm = 8. * KtPoint (2. * pi * mIdf / float (lenChain));
  vm = 0.5 * normalize (vec3 (Hashff (mIdf), Hashff (mIdf), Hashff (mIdf)) - 0.5);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat;
  vec3 rm, vm, wgBxC;
  vec2 canvas, iFrag;
  float nStep, asp;
  int mId, pxId;
  bool doInit;
  canvas = iResolution.xy;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 2 * lenChain + 2) discard;
  asp = canvas.x / canvas.y;
  wgBxC = vec3 (0.48 * asp, -0.46, 0.02);
  if (iFrame <= 5) {
    doInit = true;
    spRad = float (lenChain) / 9.;
    knType = N_KNOT;
  } else {
    doInit = false;
    stDat = Loadv4 (2 * lenChain);
    nStep = stDat.x;
    spRad = stDat.y;
    knType = int (stDat.z);
    ++ nStep;
  }
  mPtrP.z = Loadv4 (2 * lenChain + 1).z;
  if (mPtr.z > 0.) {
    if (length (mPtr.xy * vec2 (asp, 1.) - wgBxC.xy) < wgBxC.z) {
      if (mPtrP.z <= 0.) doInit = true;
    }
  }
  if (nStep > 10000.) doInit = true;
  if (doInit) {
    if (++ knType > N_KNOT) knType = 1;
    KtSetup ();
    nStep = 0.;
  }
  if (pxId < 2 * lenChain) {
    mId = pxId / 2;
    if (doInit) Init (mId, rm, vm);
    else Step (mId, rm, vm);
    if      (pxId == 2 * mId) stDat = vec4 (rm, 0.);
    else if (pxId == 2 * mId + 1) stDat = vec4 (vm, 0.);
  } else {
    if (pxId == 2 * lenChain) stDat = vec4 (nStep, spRad, float (knType), 0.);
    else if (pxId == 2 * lenChain + 1) stDat = vec4 (mPtr.xyz, 0.);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy
vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

