// by Nikos Papadopoulos, 4rknova / 2020
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define EPSILON				.25
#define PI					3.14159265359
#define RADIAN				180.0 / PI
#define SIZE                (42.)
#define RMARCH_MAX_STEPS 	25

float des_sphere(vec3 p, float r) { return length(p) - r; }
float deu_box	(vec3 p, vec3  b) { return length(max(abs(p)-b,0.0)); }
vec3  translate (vec3 v, vec3  t) { return v - t; }

const float cCamera_fov = 60.0;

struct Camera	{ vec3 p, t, u; };
struct Ray		{ vec3 o, d; };

void generate_ray(vec2 uv, Camera c, out Ray r)
{
	r.o = c.p;
	r.d = normalize(vec3(uv.x, uv.y, 1.0 / tan(cCamera_fov * 0.5 * RADIAN)));

	vec3 cd = c.t - c.p;

	vec3 rx,ry,rz;
	rz = normalize(cd);
	rx = normalize(cross(rz, c.u));
	ry = normalize(cross(rx, rz));

	mat3 tmat = mat3(rx.x, rx.y, rx.z,
			  		 ry.x, ry.y, ry.z,
					 rz.x, rz.y, rz.z);


	r.d = normalize(tmat * r.d);
}

float scene_distance(vec3 p)
{
    vec2 uv = 0.5*(p.xz/SIZE+1.);
	vec3 res = texture(iChannel0, uv).xyz;
    float d = dot(res, vec3(0,1,0));
	return deu_box(p + vec3(0,d,0), vec3(SIZE,5,SIZE));
}

vec3 scene_normal(vec3 pos, float d)
{
    vec3 n;

	// Gradient via Forward differencing
    n.x = scene_distance(vec3(pos.x + EPSILON, pos.y, pos.z));
    n.y = scene_distance(vec3(pos.x, pos.y + EPSILON, pos.z));
    n.z = scene_distance(vec3(pos.x, pos.y, pos.z + EPSILON));

    return normalize(n - d);
}

vec3 scene_shade(vec3 v, vec3 p, vec3 n)
{
    vec3 col = vec3(0.5, 0.7, 0.9);

    vec3 d = texture(iChannel1, reflect(v, n)).xyz;
    col *= vec3(d);

    return col;
}

bool rmarch(Ray r, out vec3 p, out vec3 n, out int iter)
{
	p = r.o;
	vec3 pos = p;
	float d = 1.;

	for (int i = 0; i < RMARCH_MAX_STEPS; i++) {
		iter = i;

		d = scene_distance(pos);

		if (d < EPSILON) {
			p = pos;
			break;
		}

		pos += d * r.d;
	}

	n = scene_normal(p, d);	
	return d < EPSILON;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 nv = (fragCoord.xy / iResolution.xy);	


    vec2 uv = (fragCoord.xy / iResolution.xy * 2. - 1.)
        * vec2(iResolution.x / iResolution.y, 1);	

    Camera c;
    c.p = vec3(20, 18, 14);
    c.t = vec3(-6, -4, 10);
    c.u = vec3(0, 1, 0); 


    Ray r;
    generate_ray(uv, c, r);

    vec3 col = texture(iChannel1, normalize(r.d)).xyz;    

    vec3 sp, sn;
    int iter = RMARCH_MAX_STEPS;	
    bool res = rmarch(r, sp, sn, iter);


    if (res) {
        col = scene_shade(r.d, sp, sn);    
    }


	fragColor = vec4(col, 1.0);
}

