//This parameter changes the magnification.
const float mag = 8.0;

const float GREEN = 0.0;
const float BLUE = 7.0;
const float SNOW = 2.0;
const float YELLOW = 9.0;
const float RED = 5.0;
const float WHITE = 6.0;
const float PALE_GREEN = 1.0;
const float BLACK = 8.0;
const float GRAY = 3.0;
const float ORANGE = 10.0;
//this function is from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	float num_colors = 3.0;
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return floor(fract((p3.x + p3.y) * p3.z)*num_colors);
}


float magnify(vec2 fragCoord,float mag){
	return hash12(floor(fragCoord/pow(2.0,mag)));
}

bool is_above(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord,mag) == color;
}
bool is_surrounded_by(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord+vec2(0,1),mag) == color
        && magnify(fragCoord+vec2(1,0),mag) == color
        && magnify(fragCoord-vec2(0,1),mag) == color
        && magnify(fragCoord-vec2(1,0),mag) == color;
}

bool is_next_to(vec2 fragCoord,float mag,float color){
	float offset = iTime*20.0;
    return magnify(fragCoord+vec2(offset,1),mag) == color
        || magnify(fragCoord+vec2(1,offset),mag) == color
        || magnify(fragCoord-vec2(offset,1),mag) == color
        || magnify(fragCoord-vec2(1,-offset*10.0),mag) == color;
}


vec3 get_color(float color){
    if(color == BLUE){
    	return vec3(0.149,0.141,0.912);
   	}
    else if(color == GREEN){
    	return vec3(0.000,0.833,0.224);
   	}
    else if(color == RED){
    	return vec3(1.0,0.0,0.0);
   	}
    else if(color == WHITE){
    	return vec3(1.0,1.0,1.0);
   	}
    else if(color == SNOW){
    	return vec3(255, 250, 250)/255.0;
    }
    else if(color == GRAY){
    	return vec3(192.0,192.0,192.0)/255.0;
    }
    else if(color == YELLOW){
    	return vec3(1.0,1.0,0.0);
   	}
    else if(color == ORANGE){
    	return vec3(255,127,80)/255.0;
    }
    else if(color == BLACK){
    	return vec3(0.0,0.0,0.0);
   	}
    else if(color == PALE_GREEN){
    	return vec3(152,251,152)/255.0;
    }
}

float iterations(float color1,in vec2 fragCoord,float mag){
    float mag1 = 0.0;
    while(mag1 < mag){
    bool modified = true;
    if(color1 == GREEN && is_next_to(fragCoord,mag1,PALE_GREEN)){
    	color1 = PALE_GREEN;
    }
    else if(color1 == PALE_GREEN && is_next_to(fragCoord,mag1,SNOW)){
        if(mag1 > 5.0){
        	color1 = SNOW;
        }
        else{
        	color1 = SNOW;
        }
    }
    else if(color1 == SNOW && is_next_to(fragCoord,mag1,GREEN)){
        color1 = GREEN;
    }
    else if(color1 == SNOW && is_next_to(fragCoord,mag1,PALE_GREEN) && is_next_to(fragCoord,mag1,SNOW)){
        color1 = WHITE;
    }

    mag1 += 1.0;
    }
    return color1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = fragCoord*(((iMouse.x-iMouse.y)+iResolution.x)/iResolution.x) + iTime*2.0*vec2(50.0,0.0);
    float color1 = magnify(fragCoord,mag);
    //color1 = replace_pattern(mat2(1.0,1.0,1.0,1.0),mat2(0.0,2.0,2.0,0.0),fragCoord,mag);

    color1 = iterations(color1,fragCoord,mag-1.0);

    fragColor = vec4(get_color(color1),1.0);
}

