// Gravity simulator. Written by Olli Parviainen
// Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International License.
//
// mainImage() renders the image using trajectory data from iChannel0
// a.k.a Buffer A that calculates body trajectories according to gravity law
//
// data stored to texels, one body per x-coordinate
// - coord y=0 : body XYZ + size data
// - coord y=1 : body velocity XYZ data


const int BODIES = 50;	// note: if change number of bodies, update it also to Buffer A page

const float PI = 3.1415926536;
const float PI2 = 2.0 * PI;
const float MINVAL = -1000.0;

const vec3 AMBIENT_LIGHT = 0.4 * normalize(vec3(0.5, -0.5, -1.0));

const vec4 SUN_COLOR = vec4(1,1,0,0);


vec4 getBodyPosSize(in int bodyNum)
{
    return texelFetch(iChannel0, ivec2(bodyNum, 0), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord/min(iResolution.x, iResolution.y) - vec2(iResolution.x/iResolution.y, 1.0);
    float grid = iResolution.y / 25.0;

    float min_z = MINVAL;
    vec2 force = vec2(0.0);

    vec4 body0 = getBodyPosSize(0);
    // render bodies
    for (int i = 0; i < BODIES; i ++) {
        // scale body coords&size according to z
        // To do: 
        // - could recalculate "scale" or "bodyScaled" already in Buffer A
        //   as they're now recalculated here in every pixel 
        vec4 body = getBodyPosSize(i);
        float scale = 1.0 / (1.0 - body.z);
        if (scale < 0.0) 
	        // behind camera
            continue;

        vec4 bodyScaled = scale * body;

        // calculate body visual radius 
        vec2 diff = bodyScaled.xy - uv;
        float R = dot(diff,diff);
        force += scale * pow(body.w,3.0) * diff / R;        
        float radius2 = bodyScaled.w * bodyScaled.w;

        if (R < radius2) {
            // calculate z-coordinate of the surface
            float z = sqrt(radius2 - R);       
            if (body.z + z < min_z)
                // surface point is behind what's already rendered
                continue;
            min_z = body.z + z;

            // draw body
            if (i == 0) {
                // central body
	            float lum = (1.0 - R / radius2);
                fragColor = vec4(lum) + 0.5 * SUN_COLOR;
            } 
            else 
            {
                // other bodies: choose color
                vec3 angle = vec3(i) + vec3(0.0, 0.3333, 0.6666) * PI2;            
                vec3 color=0.5+0.5*cos(angle);
                fragColor = vec4(color, 1);

                // diffuse surface lighting from central body + ambient
                vec3 surface = vec3(diff, -z);
                vec3 surface_norm = normalize(surface);
                vec3 bodySurface = body.xyz - surface;
                vec3 vecToBody0 = bodySurface - body0.xyz;
                float distToBody0 = clamp(length(vecToBody0) - body0.w, 1e-4,1e4);
                float intensity = dot(surface_norm, normalize(vecToBody0)) / distToBody0;
                intensity = 0.5 * clamp(0.2 + intensity, 0.0, 2.0);

                // direct reflection from central body
                vec3 vreflect = normalize(reflect(vec3(0,0,1), surface_norm));
                float reflectCrossBody0 = length(vreflect * length(vecToBody0) - vecToBody0);
                if (reflectCrossBody0 < body0.w) {
                    intensity += 0.2 / reflectCrossBody0;
                }

                fragColor *= dot(surface_norm, AMBIENT_LIGHT) + 
                    intensity;
            }
        }
    }
    if (min_z == MINVAL) 
    {
        // render background grid. The force field used to distort background
        // is not physically quite accurate, yet squared field in 2D looks
        // nicer than true grabity field
        vec2 coord = fragCoord - 2e2 * force.xy * grid;
        coord = mod(coord, grid);
        float color = 0.0;

        if (coord.x < 2.0) {
            color += (coord.x > 1.0) ? 2.0 - coord.x : coord.x;
        }
        if (coord.y < 2.0) {
            color += (coord.y > 1.0) ? 2.0 - coord.y : coord.y;
        }

        fragColor=vec4(0.8*sqrt(color));
        // to display 2D gravity field
        // fragColor += clamp((round(sqrt(dot(force, force))*2e3)-3.0) * 0.05, 0.0, 0.6);
    }
}

