// Gravity simulator. Written by Olli Parviainen
// Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International License.
//
// The Buffer A calculates body trajectories according to gravity physics.
//
// data stored to texels, one body per x-coordinate
// - coord y=0 : body XYZ + Size data
// - coord y=1 : body velocity XYZ data

const int BODIES = 50; // note: if change number of bodies, update it also to Buffer A page

const float GRAVITY_COEFF = 1000.0;
const float UPDATE_STEP = 0.005;
const float PI = 3.1415926536;

vec4 getBodyPosSize(in int bodyNum)
{
    return texelFetch(iChannel0, ivec2(bodyNum, 0), 0);
}


void getBodyPosVel(in int bodyNum, out vec4 XYZW, out vec3 velXYZ)
{
    XYZW = texelFetch(iChannel0, ivec2(bodyNum, 0), 0);
    velXYZ = texelFetch(iChannel0, ivec2(bodyNum, 1), 0).xyz;
}


// initialize data
void init(in int body, out vec4 XYZW, out vec3 velXYZ) 
{    
    vec4 times = vec4(iTime) + vec4(body, float(body) + 0.5 * PI, 4*body, 15*body);

    XYZW = (cos(times) + vec4(0,0,0,1.5)) * vec4(0.5, 0.5, 0.05, 0.02);
    velXYZ = 1.4*sin(times.xyz);
    if (body == 0) {
        XYZW = vec4(0.0,0.0,0.0,0.1);
        velXYZ = vec3(0);
    }
}

// calculate acceleration due to gravity
vec3 calculate_gravity_accel(in int curBodyNum, in vec4 bodyXYZW, float dTimeAdvance)
{
    vec3 accelVec = vec3(0.0);
    float bodySize = bodyXYZW.w;

    for (int i = 0; i < BODIES; i ++) {
        if (i == curBodyNum) continue;
        vec4 otherXYZW;
        vec3 otherVelXYZ;
        getBodyPosVel(i, otherXYZW, otherVelXYZ);

        otherXYZW += dTimeAdvance * vec4(otherVelXYZ.xyz, 0);

        vec3 diff = otherXYZW.xyz - bodyXYZW.xyz;
        float R = sqrt(dot(diff, diff));
        float otherSize = pow(otherXYZW.w, 3.0);
        float accel = otherSize / R; 

        accelVec += normalize(diff) * accel;
    }
    return GRAVITY_COEFF * accelVec;
}


// euler: simplest trajectory update method
void update_trajectory_euler(in int body, out vec4 XYZW, out vec3 velXYZ)
{
    vec4 bodyXYZW;
    vec3 bodyVelXyz;
    getBodyPosVel(body, bodyXYZW, bodyVelXyz);

    vec3 accelVec = calculate_gravity_accel(body, bodyXYZW, 0.0);

    velXYZ = bodyVelXyz + UPDATE_STEP * accelVec;
    XYZW = bodyXYZW + UPDATE_STEP * 0.5 * vec4(bodyVelXyz + velXYZ.xyz, 0.0);
}


// heun: two-stage trajectory update method, better than euler
void update_trajectory_heun(in int body, out vec4 XYZW, out vec3 velXYZ)
{
    vec4 bodyXYZW;
    vec3 bodyVelXyz;
    getBodyPosVel(body, bodyXYZW, bodyVelXyz);

    // step 1
    vec3 accelVec1 = calculate_gravity_accel(body, bodyXYZW, 0.0);       
    vec3 velXYZ1 = bodyVelXyz + UPDATE_STEP * accelVec1;
    XYZW = bodyXYZW + UPDATE_STEP * 0.5 * vec4(bodyVelXyz + velXYZ1, 0);

    // step 2
    vec3 accelVec2 = calculate_gravity_accel(body, XYZW, UPDATE_STEP);    
    vec3 accelVec_mid = 0.5 * (accelVec1 + accelVec2);

    velXYZ = bodyVelXyz + UPDATE_STEP * accelVec_mid;       
    XYZW = bodyXYZW + UPDATE_STEP * 0.5 * vec4(bodyVelXyz + velXYZ, 0.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 XYZW;
    vec3 velXYZ;
    ivec2 xy = ivec2(floor(fragCoord));

    if ((xy.y > 1) || (xy.x > BODIES))
        // nothing to do, release GPU early for other work
        return;

    int bodyNum = xy.x;

    if (iFrame == 0) {
        // initialize data
        init(bodyNum, XYZW, velXYZ);
    } else {
        // update trajectory
        update_trajectory_heun(bodyNum, XYZW, velXYZ);

        if (bodyNum == 0) {
            // weakly pull the body #0 towards centre to keep the system centralized on display
            // while minimizing impact to overall system
            vec3 diff = vec3(0.0) - XYZW.xyz;
            velXYZ = mix(velXYZ, diff, 0.02);
        }
    }

    // store data to output texels
    if (xy.y == 0) {
	    fragColor = XYZW;
    } else {
	    fragColor = vec4(velXYZ, 0);
    }
}

