// mud planet
// single pass CFD on spherical symetry
// based on "spilled" (https://www.shadertoy.com/view/MsGSRd)

// uncomment this for metallic reflections
//#define METAL_REFLECTIONS

#define MAX_RADIUS 400.
#define PI 3.14159265359

#define USE_CUBE

#ifndef USE_CUBE
vec2 sph2frag(vec3 p)
{
	float r = min(iResolution.x/4.,MAX_RADIUS);
    vec2 center = vec2(r,r);
    p=normalize(p);
    if(p.z<0.0) center.x += 2.*r;
    return center+sqrt(1.-abs(p.z))*normalize(p.xy)*r;
}
#endif

// final distance funtion
float dist(vec3 pos)
{
    vec3 pos0=pos;
    #ifdef USE_CUBE
    pos+=(texture(iChannel0,pos).xyz-.5)*.75;
    #else
    pos+=(texture(iChannel0,sph2frag(pos)/iResolution.xy).xyz-.5)*.75;
    #endif
    float d = 10000.;

    // sphere in the middle
	d=min(d,length(pos)-1.);

    return d;
}

vec3 getGrad(vec3 pos, float eps)
{
    vec2 d=vec2(eps,0);
    float d0=dist(pos);
    return vec3(dist(pos+d.xyy)-d0,
                dist(pos+d.yxy)-d0,
                dist(pos+d.yyx)-d0)/eps;

}

// march it...
vec4 march(inout vec3 pos, vec3 dir)
{
    // cull the sphere
    if(length(pos-dir*dot(dir,pos))>1.3) 
    	return vec4(0,0,1,1);

    float eps=0.001;
    float bg=1.0;
    for(int cnt=0;cnt<100;cnt++)
    {
        float d = dist(pos);
        pos+=d*dir*.7;
        if(d<eps) { bg=0.0; break; }
    }
    vec3 n = getGrad(pos,.001);
    if(dot(n,n)<.0001) n=vec3(0,0,1);
    return vec4(n,bg); // .w=1 => background
}

mat3 rotX(float ang)
{
    float c=cos(ang), s=sin(ang);
    return mat3(1,0,0, 0,c,s, 0,-s,c);
}

mat3 rotZ(float ang)
{
    float c=cos(ang), s=sin(ang);
    return mat3(c,s,0, -s,c,0, 0,0,1);
}

vec3 getSun()
{
    float phi=.2*iTime;
    float phi2=.7*cos(phi);
    vec3 sun=vec3(vec2(cos(phi),sin(phi))*cos(phi2),sin(phi2));
    return sun;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // screen coord -1..1
    vec2 sc = (fragCoord.xy/iResolution.xy)*2.-1.;
    // viewer position
    vec3 pos = vec3(0,-13.5,0);
    // pixel view direction
    vec3 dir = normalize(8.*normalize(-pos)+vec3(sc.x,0,sc.y*iResolution.y/iResolution.x));
    // rotate view around x,z
    float phi = iMouse.x/iResolution.x*7.;
    float th  = iMouse.y/iResolution.y*7.;
    if (iMouse.x==0.) { phi=iTime*.5; th=.27*.5*iTime; }
    mat3 rx = rotX(th);
    mat3 rz = rotZ(phi);
    pos = rz*(rx*pos);
    dir = rz*(rx*dir);
    vec3 eye = pos;

    vec3 sun = getSun();

    // march it...
   	vec4 n=march(pos,dir);
    float bg=n.w;

    // march the shadow
    vec3 poss=pos+sun*.01;
   	vec4 n2=march(poss,sun);
    float shadow = n2.w;

    // calc some ambient occlusion
    float ao=1.;
    // calc ao by stepping along normal
    ao*=dist(pos+normalize(getGrad(pos,.1))*.1)/.1;
    // adjust contrast of ao
    //ao=pow(ao,.4);

    // reflection dir
    vec3 R = pos-2.0*dot(pos,n.xyz)*n.xyz;
    R = -((R*rz)*rx).yzx;

    vec3 c = vec3(1);

    float diff=clamp(clamp(dot(sun,n.xyz)*2.,0.,1.)*shadow+.3,0.,1.5);
    // simply add some parts of the normal to the color
    // gives impression of 3 lights from different dir with different color temperature
    c += normalize(n.xyz)*.1+.1;

    //c *= 1.*length((texture(iChannel0,sph2frag(pos)/iResolution.xy).xyz-.5)*2.+.5);
    //c *= (texture(iChannel0,sph2frag(pos)/iResolution.xy).xyz-.5)*.3+.5;
    c*=.8;

    //  reflection of cubemap
    float fres = 1.-(dot(normalize(eye-pos),n.xyz));
    fres=.1+.9*fres;
    #ifdef METAL_REFLECTIONS
    c *= texture(iChannel1,R).xyz/**1.2*fres*/;
    #else
    //c += texture(iChannel1,R).xyz*fres;
    #endif

    // specular from sun
    c += clamp(pow(max(0.,dot(R,sun)),.5)*fres,0.,1.);

    // apply background
    if(bg>.5) { diff=0.0; ao=1.; }
    float sdot=clamp(dot(sun,dir),0.,1.);
    c=mix(c,vec3(.03,.13,.28),1.-diff);

    // apply ambient occlusion
    c*=ao;

    // draw sun
    float sunmix=(pow(sdot,600.)*2.+.3*sdot);
    c=c*(.8-2.*sunmix)+vec3(1)*bg*sunmix;

    // vignetting
    float vign = (1.06-.3*length(sc.xy));

	fragColor = vec4(c*vign,1);
    //fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}

