/* Inspired by https://thebookofshaders.com/07/ */

#define PI 3.14159265
#define rot(a)   mat2(cos(a),-sin(a),sin(a),cos(a))

vec4 DrawPetalPolar(vec2 uv, vec2 pos, float size, vec2 dir){

    //Distance
    vec2 dist = uv - pos;

    //Rotate
    float angle = -atan(dir.y, dir.x);

    dist *= rot(angle);

    //Translate
    dist.x -= size * 0.25f;

    //Polar coordinates
    float r = length(dist)*2.0;
    float a = atan(dist.y, dist.x);

    //Shaping function
    float f = -1.;

    if(a > PI*0.5 || a < -PI*0.5){
    	f = size * cos(a*2.);
    }

    return vec4(1. - smoothstep(-.8, .8, (r-f)/fwidth(r-f)));
}

vec4 DrawPetalDistance(vec2 dist, float size, vec2 dir){

    dist.y *= 5.;

    float d = length(dist);

    return vec4(smoothstep(size - 0.01, size + 0.01, d));

}

