// Signed distance and gradient to a hexagon. Optimized to the best of my ability but not profiled.

// List of iq's other 2D distances+gradients:
//
// Circle:   https://www.shadertoy.com/view/WltSDj
// Box:      https://www.shadertoy.com/view/wlcXD2
// Cross:    https://www.shadertoy.com/view/WtdXWj
// Segment:  https://www.shadertoy.com/view/WtdSDj

// return distance (.x) and gradient (.yz)
vec3 sdgHexagon(vec2 p, float r) {
    const vec4 K = vec4(sqrt(3.0), 0.5 * sqrt(3.0), 1.0 / sqrt(3.0), 2.0 / sqrt(3.0));
    vec3 a = vec3(abs(p),0);

    if(K.x * a.x <= min(a.y, r)) {
        // gradient is vertical
        a.z = a.y - r;
        a.xy = vec2(0.0, -1.0);
    } else if(abs(a.z = dot(a.xy, vec2(K.y, -1.5))) <= r) {
        // gradient is 30 degrees from horizontal
        a.z = a.z + a.y + a.y - r;
        a.xy = vec2(-K.y, -0.5);
    } else {
        // gradient is towards a sharp corner
        a.z = length(a.xy = a.z < r ? vec2(K.z * r - a.x, r - a.y) : vec2(K.w * r - a.x, -a.y));
        a.xy = vec2(a.x / a.z, a.y / a.z);
    }
    return vec3(a.z, p.x < 0. ? -a.x : a.x, p.y < 0. ? -a.y : a.y);
}

void mainImage(out vec4 O, vec2 U) {

    float mult = iResolution.y;
    float mult3 = 3.0/mult;
    vec2 pos = U-0.5*iResolution.xy;

    // size
	float b = floor(0.125*iResolution.y);

    // corner radius
    float ra1 = (0.5+0.5*sin(iTime*1.2))*0.25*mult;
    float ra2 = (0.5+0.5*sin(iTime*1.3))*0.25*mult;

    float center = 0.5*(ra1+ra1+ra2);
    float radius = 0.5*ra2;

    // sdf(p) and gradient(sdf(p))
	vec3 dg = sdgHexagon(pos,b);
    float d = abs(dg.x-center)-radius;
    vec2 g = dg.yz*sgn(dg.x-center);

    // central differenes based gradient, for comparison
    // g = vec2(dFdx(d),dFdy(d))/(2.0/iResolution.y);

	// coloring
    vec3 col = vec3(0.5+0.5*g,0.5+0.5*sign(d));
	col *= 1.0 - 0.5*exp2(-16.0*abs(d*mult3));
	col *= 0.75 + 0.25*cospi(64.0*d*mult3);
	col = mix(vec3(1.0), col, smoothstep(0.0,0.01,abs(d*mult3)));

    O = vec4(col,1.0);
}

