//This parameter changes the magnification.
const float mag = 8.0;

const float RED = 0.0;
const float BLUE = 7.0;
const float GRAY = 2.0;
const float YELLOW = 3.0;
const float GREEN = 5.0;
const float WHITE = 6.0;
const float ORANGE = 1.0;
const float BLACK = 8.0;
//this function is from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	float num_colors = 3.0;
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return floor(fract((p3.x + p3.y) * p3.z)*num_colors);
}


float magnify(vec2 fragCoord,float mag){
    fragCoord += vec2(sin(fragCoord.y/(100.0*mag)+iTime)*10.0,sin(fragCoord.x/(100.0*mag)+iTime)*10.0)*mag*mag;

    return hash12(floor(fragCoord/pow(2.0,mag)));
}

bool is_next_to(float color,vec4 neighbors){
	return (neighbors.x == color)
        || (neighbors.y == color)
        || (neighbors.z == color)
        || (neighbors.w == color);
}



vec3 get_color(float color){
    if(color == BLUE){
    	return vec3(0.149,0.141,0.912);
   	}
    else if(color == GREEN){
    	return vec3(0.000,0.833,0.224);
   	}
    else if(color == RED){
    	return vec3(1.0,0.0,0.0);
   	}
    else if(color == WHITE){
    	return vec3(1.0,1.0,1.0);
   	}
    else if(color == GRAY){
    	return vec3(192.0,192.0,192.0)/255.0;
    }
    else if(color == YELLOW){
    	return vec3(1.0,1.0,0.0);
   	}
    else if(color == ORANGE){
    	return vec3(255,127,80)/255.0;
    }
    else if(color == BLACK){
    	return vec3(0.0,0.0,0.0);
   	}
}

float iterations(float color1,in vec2 fragCoord,float mag){
    float mag1 = 0.0;
	vec4 neighbors;
    float offset = iTime*20.0;
    float offset1 = offset*20.0;
    while(mag1 < mag){
    neighbors = vec4(magnify(fragCoord+vec2(offset,1),mag1),
        magnify(fragCoord+vec2(1,offset),mag1),
        magnify(fragCoord-vec2(offset,1),mag1),
        magnify(fragCoord-vec2(1,offset1),mag1));
    bool modified = true;

    if(color1 == RED && is_next_to(ORANGE,neighbors)){
    	color1 = ORANGE;
    }
    else if(color1 == ORANGE && is_next_to(GRAY,neighbors)){
        if(mag1 > 5.0){
        	color1 = YELLOW;
        }
        else{
        	color1 = GRAY;
        }
    }
    else if(color1 == GRAY && is_next_to(RED,neighbors)){
        color1 = RED;
    }
    else if(color1 == YELLOW && is_next_to(GRAY,neighbors)){
        color1 = RED;
    }
    else if(color1 == GRAY && is_next_to(ORANGE,neighbors) && is_next_to(GRAY,neighbors)){
        color1 = WHITE;
    }

    mag1 += 1.0;
    }
    return color1;
}

