// anti-aliasing samples. set to 0 to disable AA.
#define AA 2

const float PI=acos(-1.);
const float EPS=.001,MAX=100.;
const int ITER=256;

float ycyl(vec3 p, float r) {
    return length(p.xz)-r;
}

float sph(vec3 p, float r) {
    return length(p)-r;
}

float ztor(vec3 p, vec2 r) {
    vec2 circ=vec2(length(p.xz)-r.x,p.y);
    return length(circ)-r.y;
}

float scene(vec3 p) {
    float d=MAX;
    d=min(d,ycyl(p,.5));
    d=min(d,sph(abs(p)-1.,.6));
    d=min(d,ztor(p, vec2(1., .3)));
    return d;
}

vec3 normal(vec3 p){
    vec2 D=vec2(.001,0);
    return normalize(scene(p)-vec3(scene(p-D.xyy),scene(p-D.yxy),scene(p-D.yyx)));
}

float march(vec3 ro, vec3 rd){
    float t=0.,d;
    for(int i=0; i<ITER; ++i){
        d=scene(ro+rd*t);
        if(abs(d)<EPS) return t;
        t+=d;
        if(t>MAX) return t;
    }
    return t;
}

vec3 ray(vec2 uv, vec3 ro, vec3 poi){
    vec3 f=normalize(poi-ro),
         r=normalize(cross(f,vec3(0,1,0))),
         u=cross(r,f),
         c=ro+f,
         i=c+r*uv.x+u*uv.y;
    return normalize(i-ro);
}

vec3 render(vec2 uv){
    float orbit=-2.*PI*iMouse.x/iResolution.x;
    vec3 ro;
    if (iMouse.z>0.)
        ro=vec3(sin(orbit)*5.,-2.*iMouse.y/iResolution.y+1.,cos(orbit)*5.);
    else
        ro=vec3(sin(iTime),cos(iTime),5.);
    vec3 rd=ray(uv,ro,vec3(0));
    float t=march(ro,rd);
    if(t>=MAX) return texture(iChannel0,rd).rgb;
    vec3 p=ro+rd*t,n=normal(p);
    return texture(iChannel0,refract(rd,n,.75)).rgb;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col=vec3(0);
#if AA>1
    vec2 aao;
    const float AAINC=1./float(AA);
    for (aao.x=-.5; aao.x<.5; aao.x+=AAINC)
        for (aao.y=-.5; aao.y<.5; aao.y+=AAINC)
            col+=render(uv+aao/iResolution.y);
    col/=float(AA*AA);
#else
    col+=render(uv);
#endif
    fragColor=vec4(col,1.);
}

// variant of https://shadertoy.com/view/WlVXzK
// variant of https://shadertoy.com/view/ttKSDm

float PI = 3.14159,
       r = .15,                                                   // wire radius
      dr = .1,                                                    // yarn radius (but section totally wrong)
       d = 1.32, e = 4.5, f = 3., s, t0=1e9;

#define FRAME 0

#define S(a,da)  ( abs(mod(atan(q.y,q.x)-a+PI, 2.*PI) -PI) - da ) // section
#define Ts(q,a,da) max(T(q),S(a,da) )                             // torus section
#define R(a)       mat2( cos( a + vec4(0,33,11,0)) )              // rotation
#define CS(a)      vec2(cos(a),sin(a))
#define T0(q)     ( length(vec2(length(q.xy)-1.,q.z)) - r-dr )      // torus
#define Ts0(q,a,da) max(T0(q),S(a,da) )                             // torus section

float T(vec3 q) { // global s for yarn orientation
    vec3 c=q; float t, a;     
        a = atan(c.y,c.x); 

    q = vec3( length(c.xy) - 1., c.z,  a*5.9*s ), 
    a = atan(q.y,q.x); 
    q.xy -= r * CS(-floor(q.z-a)+q.z);
    t = length(q.xy) - dr;
    return t*=.5;
}

float knot0(vec3 p) {                           // --- half stitch
    vec3 q; float t, a=p.y+f/4.;
  //p.z += .4*(1.-.5*a*a),                      // depth twist
    p.z += .4*cos(1.5*a),                       // depth twist
    p.x = mod(p.x-d,2.*d)-d;                    // + rank periodicity
    q = p, t = Ts(q,PI/2.,1.55*PI/2.);          // half stitch
    t0 = min(t0,Ts0(q,PI/2.,1.55*PI/2.));        // blur coloring attempt
    return t;
}
float knot(vec3 p) {                            // --- full rank of stitch
    vec3 q=p; float t;
    q.y = mod(q.y,e)-f,                         // + vertical periodicity
    s = 1.; t = knot0(q);                                    // top half stitch
    s= -1.; q.y = -1.5-q.y,  q.x -= d, t = min(t, knot0(q)); // bottom symmetric
    return t;
}

void mainImage(out vec4 O, vec2 U) {
    float t = 1e9, tx,ty,tz, E = 0.;
    vec3  q = iResolution,
          D = normalize(vec3(.3*(U+U-q.xy)/q.y, -1)),     // ray direction
          P = 20./q, p, a, qa;                 // marching point along ray 
    vec2  M = (length(iMouse.xy)<20.) ? vec2(0): 3.14*(2.*iMouse.xy-q.xy)/q.y,
        //C = 3.14*vec2(-.3,.4),               // camera
          C = M==vec2(0) ? vec2(iTime) : M;
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.04 )
        p = P,
        p.xz *= R(C.x), p.yz *= R(C.y),        // camera rotation
        q = p,
#if FRAME
        a = abs(qa=q),                         // draw frame
        t = max(min( ty= max(a.x,a.z) -.01, min( tx= max(a.z,a.y) -.01 ,tz= max(a.x,a.y) -.01 )  ),length(q)-2.),
#endif
        t = min(t, knot(p)),                   // odd rank
        p.y -= e/2., 
        t = min(t, knot(p)),                   // even rank
        E += .001/t,                           // blur coloring attempt
        P += t*D;                              // step forward = dist to obj

    O *= 1.5; 
 // O.r += E;                                  // coloring attempts
 // O.r += exp(-10.*t0);
 // O = O*vec4(1,0,0,1) + exp(-20.*abs(t0));

#if FRAME                                      // --- color frame
    if (t==tx) O.gb*=.4+.4*sign(qa.x);         // x axis: red   ( whiter on positive side )
    if (t==ty) O.rb*=.4+.4*sign(qa.y);         // y axis: green
    if (t==tz) O.rg*=.4+.4*sign(qa.z);         // z axis: blue
#endif
}

