// Adaptive Exposure by Hazel Quantock
// This work is licensed under a Creative Commons Attribution-ShareAlike 4.0 International License. https://creativecommons.org/licenses/by-sa/4.0/

// blur the image so I can sample exposure locally

const vec2 exposureRadius = vec2(.03); // low values mean space and ground and horizon can all be exposed at once!
// this adds a bit of a comic book feel to it - where details are emphasized more than overall brightness

const float globalExposure = .4; // 0.0 = use only nearby exposure, 1.0 = sample exposure over entire image, 0.5 = 50/50 blend

vec2 BoxMuller( vec2 rand )
{
//    float r = sqrt(-2.*log(rand.x));
    float a = tau * rand.y;

// alternative curve, to hang onto small details AND do larger ones
float r = -2.*log(max(.0001,rand.x));
//float r = pow(-2.*log(rand.x),3.); // push it even further - weird

    return r*vec2(sin(a),cos(a));
}


// blurred image of Buffer A
void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    /*would like it to be resolution independent
	=> take random samples from buf a spread over a gaussian (normal) distribution
	and feedback, so high res just takes longer to converge*/
    // ooh box muller transform: https://en.wikipedia.org/wiki/Box%E2%80%93Muller_transform

    vec2 blurRadius = sqrt(iResolution.x*iResolution.y) * exposureRadius;

    fragColour = vec4(0);
    const int numPasses = 10;
    for ( int pass = 0; pass < numPasses; pass++ )
    {
        SeedRand( iFrame*numPasses+pass, fragCoord );

    	vec2 tappos;
        if ( rand() > globalExposure )
        {
        	tappos = BoxMuller( rand2() ) * blurRadius;
            tappos = clamp(fragCoord+tappos,vec2(0),iResolution.xy-1.);
        }
        else
        {
            tappos = rand2()*(iResolution.xy-1.);
        }

        fragColour += texelFetch( iChannel0, ivec2(tappos), 0 );
	}

    // this might need *.99 or something
    if ( iFrame > 0 && iMouse.z <= 0. )
    {
        fragColour += texelFetch( iChannel1, ivec2(fragCoord), 0 );
    }
}

