// by natpbs
// suggested usage: click the 'pause' button
// and drag the mouse around on the canvas

//#define DRAW_GRID
#define FAST_VERSION
#define DEBUG

struct vec5 {
 vec2 a;
 vec3 b;
};

struct mat5 {
 mat2 a; mat2x3 b;
 mat3x2 c; mat3 d;
};

const float tau = 2.*acos(-1.);
const float a = sqrt(2./5.);
const float b = 2.*acos(-1.)/5.;
const mat5 B = mat5(
 a * mat2(
  1.,cos(b),
  0.,sin(b)
 ),
 a * mat2x3(
  cos(2.*b),cos(3.*b),cos(4.*b),
  sin(2.*b),sin(3.*b),sin(4.*b)
 ),
 a * mat3x2(
  1.,cos(2.*b),
  0.,sin(2.*b),
  sqrt(.5),sqrt(.5)
 ),
 a * mat3(
  cos(4.*b),cos(b),cos(3.*b),
  sin(4.*b),sin(b),sin(3.*b),
  sqrt(.5),sqrt(.5),sqrt(.5)
 )
);

vec3 offset3;
vec5 offset5;

vec5 fiveFromTwo(vec2 p2) {
 vec5 p5 = vec5(
  B.a * p2,
  B.b * p2
 );
 return p5;
}

vec5 fiveFromThree(vec3 p3) {
 vec5 p5 = vec5(
  B.c * p3,
  B.d * p3
 );
 return p5;
}

vec2 twoFromFive(vec5 p5) {
 vec2 p2 = p5.a * B.a + p5.b * B.b;
 return p2;
}

vec3 threeFromFive(vec5 p5) {
 vec3 p3 = p5.a * B.c + p5.b * B.d;
 return p3;
}

vec5 snapToGrid(vec5 p) {
 // note that floor(x+.5)
 p.a = floor(.5 + p.a);
 p.b = floor(.5 + p.b);
 return p;
}

vec5 getNeighbor(vec5 p, int k) {
 float sig = (k&1) == 0 ? 1. : -1.;
 k >>= 1;
 if (k<2) {
  p.a[k] += sig;
 } else {
  p.b[k-2] += sig;
 }
 return p;
}

bool isInWindow(vec5 p5) {
 vec3 p = threeFromFive(p5);
    p -= offset3;
 const vec3 base[5] = vec3[](
  vec2(1,0) * B.c,
  vec2(0,1) * B.c,
  vec3(1,0,0) * B.d,
  vec3(0,1,0) * B.d,
  vec3(0,0,1) * B.d
 );
	for (int i=0; i<4; i++) {
		for (int j=i+1; j<5; j++) {
			vec3 n = normalize(cross(base[i],base[j]));
			float d = abs(dot(p,n));
            float t = 0.;
            for (int k=0; k<5; k++) {
                if (k==i || k==j) {
                    continue;
                }
                t += abs(dot(base[k],n));
            }
            if (d > .5*t) {
                return false;
            }
		}
	}
 return true;
}

float drawPoint(vec2 p, vec2 q) {
 float d = distance(p,q);
 d = smoothstep(-.1,0.,-d);
 return d;
}

float drawLine(vec2 p, vec2 a, vec2 b) {
 vec2 ap = p - a;
 vec2 ab = b - a;
 float t = dot(ap,ab) / dot(ab,ab);
 t = clamp(t,0.,1.);
 vec2 q = a + t * ab;
 float d = distance(p,q);
 d = smoothstep(-.03,0.,-d);
 return d;
}

float drawGrid(vec5 p, vec5 q) {
 vec2 a = abs(p.a - q.a);
 vec3 b = abs(p.b - q.b);
 float d = max(
  max(a.x,a.y),
  max(b.x,max(b.y,b.z))
 );
 d = smoothstep(.48,.5,d);
 return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
 fragColor = vec4(vec3(0),1);
 vec2 p2 = (gl_FragCoord.xy - .5*iResolution.xy) / iResolution.x;
 p2 *= 16.;
 vec5 p5 = fiveFromTwo(p2);
    if (iMouse.z > 0.) {
        offset3 = vec3(
            1. * (iMouse.xy - .5*iResolution.xy) / iResolution.x,
            0
        );
        offset5 = fiveFromThree(offset3);
        p5.a += offset5.a;
        p5.b += offset5.b;
    } else {
        offset3 = vec3(0);
        offset5 = vec5(vec2(0),vec3(0));
    }
 vec5 q5 = snapToGrid(p5);
 vec2 q2 = twoFromFive(q5);
 vec3 q3 = threeFromFive(q5);
 float d = length(q3);
 fragColor.r += drawPoint(p2,q2);
 int count = 0;
 for (int j=0; j<10; j++) {
  vec5 r5 = getNeighbor(q5,j);
  if (isInWindow(r5)) {
   count++;
   vec2 r2 = twoFromFive(r5);
   fragColor.r += drawPoint(p2,r2);
   fragColor.b += drawLine(p2,q2,r2);
   #ifndef FAST_VERSION
   for (int k=0; k<10; k++) {
       if ((k|1)==(j|1) && (k&1) != (j&1)) {
           continue;
       }
    vec5 s5 = getNeighbor(r5,k);
    if (isInWindow(s5)) {
     vec2 s2 = twoFromFive(s5);
     fragColor.r += drawPoint(p2,s2);
     fragColor.b += drawLine(p2,r2,s2);
    }
   }
   #endif
  }
 }
    #ifdef DRAW_GRID
		fragColor.rgb += .4*drawGrid(p5,q5);
    #endif
    #ifdef DEBUG
 if (!isInWindow(q5)) {
  fragColor.rb = vec2(1.);
 }
 count = 0
  | ((count & 1) << 4)
  | ((count & 2) << 1)
  | ((count & 4) >> 1)
  | ((count & 8) >> 5);
 fragColor.rgb += float(count) / 64.;
    #endif
}

#define PI 3.141592653589793

float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

     vec2 uv = (fragCoord.xy/iResolution.xy);

    float rad1 = cos(PI/3.);
    float rad2 = sin(PI/3.);

    float io1 = step(.5, mod(uv.y*10., 1.0));

    float line2 = mod(uv.y * 10. *rad1 + uv.x *10. * rad2, 1.);
    float io2 = step(.5, line2);

    float line3 = mod(uv.y * 10. *rad1 - uv.x *10. * rad2 , 1.);
    float io3 = step(.5, line3);

    float io = (io1 + io2 + io3)/3.;

    fragColor = vec4(vec3(io), 1.);

}

// anti-aliasing samples. set to 0 to disable AA.
#define AA 2

// how many times to calculate bounced reflection rays.
#define REFLECT 2

const float PI=acos(-1.);
const float EPS=.001,MAX=100.;
const int ITER=256;

float ycyl(vec3 p, float r) {
    return length(p.xz)-r;
}

float sph(vec3 p, float r) {
    return length(p)-r;
}

float ztor(vec3 p, vec2 r) {
    vec2 circ=vec2(length(p.xz)-r.x,p.y);
    return length(circ)-r.y;
}

float scene(vec3 p) {
    float d=MAX;
    d=min(d,ycyl(p,.5));
    d=min(d,sph(abs(p)-1.,.6));
    d=min(d,ztor(p, vec2(1., .3)));
    return d;
}

vec3 normal(vec3 p){
    vec2 D=vec2(.001,0);
    return normalize(scene(p)-vec3(scene(p-D.xyy),scene(p-D.yxy),scene(p-D.yyx)));
}

float march(vec3 ro, vec3 rd){
    float t=0.,d;
    for(int i=0; i<ITER; ++i){
        d=scene(ro+rd*t);
        if(abs(d)<EPS) return t;
        t+=d;
        if(t>MAX) return t;
    }
    return t;
}

vec3 ray(vec2 uv, vec3 ro, vec3 poi){
    vec3 f=normalize(poi-ro),
         r=normalize(cross(f,vec3(0,1,0))),
         u=cross(r,f),
         c=ro+f,
         i=c+r*uv.x+u*uv.y;
    return normalize(i-ro);
}

vec3 render(vec2 uv){
    float orbit=-2.*PI*iMouse.x/iResolution.x;
    vec3 ro;
    if (iMouse.z>0.)
        ro=vec3(sin(orbit)*5.,-2.*iMouse.y/iResolution.y+1.,cos(orbit)*5.);
    else
        ro=vec3(sin(iTime),cos(iTime),5.);
    vec3 rd=ray(uv,ro,vec3(0));
    vec3 col=texture(iChannel0,rd).rgb;
    float t;
    vec3 p,n;
    for(int i=0; i<REFLECT; ++i){
        t=march(ro,rd);
        if(t>=MAX) return col;
        p=ro+rd*t;
        n=normal(p);
        rd=reflect(rd,n);
        ro=p+rd*EPS;
        col=texture(iChannel0,rd).rgb;
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col=vec3(0);
#if AA>1
    vec2 aao;
    const float AAINC=1./float(AA);
    for (aao.x=-.5; aao.x<.5; aao.x+=AAINC)
        for (aao.y=-.5; aao.y<.5; aao.y+=AAINC)
            col+=render(uv+aao/iResolution.y);
    col/=float(AA*AA);
#else
    col+=render(uv);
#endif
    fragColor=vec4(col,1.);
}

const int ITER=256;
const float MAX=100.,EPS=.001;

vec3 ray(vec2 uv, vec3 ro, vec3 poi) {
    vec3 f=normalize(poi-ro),
         r=normalize(cross(vec3(0,1,0),f)),
         u=cross(f,r),
         m=ro+f,
         p=m+r*uv.x+u*uv.y;
    return normalize(p-ro);
}

float zcyl(vec3 p, float r) {
    return length(p.xy)-r;
}

float scene(vec3 p) {
    return .5-zcyl(p,.5);
}

float map(vec3 ro, vec3 rd) {
    float t=0.,d;
    for(int i=0; i<ITER; ++i) {
        d=scene(ro+rd*t);
        if(abs(d)<EPS) return t;
        t+=d;
        if(t>MAX) return t;
    }
    return t;
}

vec3 normal(vec3 p) {
    const vec2 D=vec2(.001,0);
    return normalize(scene(p)-vec3(scene(p-D.xyy),scene(p-D.yxy),scene(p-D.yyx)));
}

vec3 march(vec2 uv) {
    vec3 ro=vec3(sin(iTime)/2.,sin(iTime*.8)*.2,cos(iTime)/2.),rd=ray(uv,ro,vec3(0));

    float t=map(ro, rd);

    if (t<MAX) {
        vec3 p=ro+rd*t;
        vec3 n=normal(p);
        vec2 uv=vec2(p.z,atan(p.y,p.x));
        vec3 tex=texture(iChannel0,uv-vec2(iTime,.0)).rgb;
        return tex/max(abs(-p.z),1.);
    }

    return vec3(0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col=march(uv);
    fragColor=vec4(col,1);
}

// variant of https://shadertoy.com/view/ttKSDm

float PI = 3.14159,
       r = .16,                                // rope radius
       d = 1.32, e = 4.5, f = 3.;

#define FRAME 0

#define T(q)     ( length(vec2(length(q.xy)-1.,q.z)) - r )        // torus
#define S(a,da)  ( abs(mod(atan(q.y,q.x)-a+PI, 2.*PI) -PI) - da ) // section
#define Ts(q,a,da) max(T(q),S(a,da) )                             // torus section
#define R(a)       mat2( cos( a + vec4(0,33,11,0)) )              // rotation

float knot0(vec3 p) {                           // --- half stitch
    vec3 q; float t, a=p.y+f/4.;
  //p.z += .4*(1.-.5*a*a),                      // depth twist
    p.z += .4*cos(1.5*a),                       // depth twist
    p.x = mod(p.x-d,2.*d)-d;                    // + rank periodicity
    q = p, t = Ts(q,PI/2.,1.55*PI/2.);          // half stitch
    return t;
}
float knot(vec3 p) {                            // --- full rank of stitch
    vec3 q=p; float t;
    q.y = mod(q.y,e)-f,                         // + vertical periodicity
    t = knot0(q);                                    // top half stitch
    q.y = -1.5-q.y,  q.x -= d, t = min(t, knot0(q)); // bottom symmetric
    return t;
}

void mainImage(out vec4 O, vec2 U) {
