#define AA 2
#define ZOOMTOGGLE 0
#define OFFSETX -1.1
#define OFFSETY -0.2

/*
	Shader Sundays! (9/52) 
	"Mandelbrot&Julia Explorer"

	The inspiration for this shader came from watching this very cool video from numberphile
	Where they explain the relationship between the Mandelbrot & Julia set, I wanted to see 
	it for myself so here it is. There's some hacked in zoom I thought to put in last min,
	because you can't have a mandelbrot set without zoom. The color gradient I got from the 
	recent IQ shader. 

	Numberphile vid:
	https://youtu.be/FFftmWSzgmk
	IQ Mandelbrot Shader:
	https://www.shadertoy.com/view/ttVSDW
*/

vec2
square(vec2 Z)
{
    //a^2 - b^2 + 2abi
    return vec2(Z.x* Z.x - Z.y * Z.y, 2.0*Z.x*Z.y);
}

float
mandelbrot(vec2 uv, const int max_steps, float zoom, vec2 offset)
{
    float res = -1.0;
    vec2 C = uv*zoom + offset;
    vec2 Z  = vec2(0.0);

    for(int i = 0; i < max_steps; ++i)
    {
        Z = square(Z) + C;

        if(length(Z) > 2.0)
        {
            res = float(i);
            break;
        }
    }

    return res;
}

float
julia(vec2 uv, const int max_steps, float zoom, vec2 offset)
{
    float res = -1.0;
    vec2 C = offset;
    vec2 Z = uv;

    for(int i = 0; i < max_steps; ++i)
    {
        Z = square(Z) + C;

        if(length(Z) > 2.0)
        {
            res = float(i);
            break;
        }
    }

    return res;
}

void
mainImage(out vec4 fragColor, in vec2 fragPos)
{
    //Setup
    float time = iTime / 10.0;
    float AR = iResolution.x / iResolution.y;
    vec3 col = vec3(0.0);
    vec3 tot = vec3(0.0);
    vec2 mouse = (iMouse.xy - 0.5*iResolution.xy) / iResolution.y;

    //SS AA loop
    for(int i = 0; i < AA; ++i)
    for(int j = 0; j < AA; ++j)
    {
        vec2 offset = vec2(i, j) / float(AA) - 0.5;

        vec2 muv = ((fragPos + offset) - 0.5*iResolution.xy ) / iResolution.y;
        vec2 uv = ((fragPos + offset)) / iResolution.xy;

        //Minimap setup
        vec2 uv2 = uv;
        uv2.x = 1.0 - uv2.x;
        uv2 *= vec2(2.6, 2.6);
        vec2 id = floor(uv2);
        uv2 = vec2(1.0 - uv2.x, uv2.y);
        vec2 juv = (fract(uv2) - 0.5);
        juv.x *= AR;     
		float inJulia = saturate(1.0 - id.y) * saturate(1.0 - id.x); 
        float inMandelbrot = 1.0 - inJulia;

        //Zoom 
        const int max_steps = 500;
        float zoom = 1.0;
        #if ZOOMTOGGLE
        zoom = (sin(time)* 0.5 + 0.5)*float(ZOOMTOGGLE);
        #endif

        //Offset and Mouse movement
        vec2 mov = vec2(OFFSETX, OFFSETY);
        vec2 movement = mov + mouse.xy;

    	//Num Steps for Mandelbrot and Julia
        float mI = mandelbrot(muv, max_steps, zoom, mov );        
        float jI = julia(juv, max_steps, zoom, movement);

        //Applying mandelbrot and julia colors
        col += (mI<0.0) ? vec3(0.0) : 0.5 + 0.5*cos( pow(zoom,0.22)*mI*0.05 + vec3(7.0,2.0,3.0));
        col *= inMandelbrot;
        col += (jI<0.0) ? vec3(0.0) : (0.5 + 0.5*cos( pow(zoom,0.21)*jI*0.05 + vec3(2.0,2.0,5.0))) * inJulia;     

        //Mouse markers
        col += (length(muv - mouse.xy) < 0.005) ? vec3(1.0) : vec3(0.0);
        col += (length(juv - mouse.xy) < 0.01) ? vec3(1.0) * inJulia : vec3(0.0) ;

        tot += col;
    }
    tot /= float(AA*AA);

    GAMMA(tot);
    fragColor = vec4(tot, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 mouseUV = iMouse.xy/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float d = length(uv);
    float r = 0.3*sin(1.);
    float ctf = max((sin(0.5)+cos(iTime)), 0.25)/2.;
    float c = smoothstep(r-0.005, r, d)+1.;
    if(d > r) c *= ctf+0.5; else c *= 1.5-ctf;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col *= vec3(c);
    // Output to screen
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Snub hexagonal tiling plus dual.
// cf. https://www.shadertoy.com/view/3tyXWw by fizzer
//
// <mouse>: translate, or move Wythoff point
// d: show dual
// f: show fundamental region
// m: change mouse mode
// r: show basic grid regions
// t: show tiling
// z: auto zoom
//
// Moving the Wythoff point makes the tiling non-uniform and in some
// positions, the tiling not drawn completely.
//
////////////////////////////////////////////////////////////////////////////////

const float X = 1.732050808; // sqrt(3)
const float Y = 0.577350269; // 1/sqrt(3)

const mat2 M = 0.5*mat2(1,-X,1,X);
const mat2 Minv = Y*mat2(X,X,-1,1);

vec2 reflection(vec2 p, vec2 q, vec2 r) {
  // reflect p in the line through q and r
  p -= q;
  vec2 n = (r-q).yx * vec2(1,-1);
  p -= 2.0*dot(p,n)*n/dot(n,n);
  p += q;
  return p;
}

float segment(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

float line(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p-a;
  vec2 ba = b-a;
  float h = dot(pa,ba)/dot(ba,ba);
  return length(pa - ba * h);
}

const int CHAR_D = 68;
const int CHAR_F = 70;
const int CHAR_G = 70;
const int CHAR_M = 77;
const int CHAR_R = 82;
const int CHAR_T = 84;
const int CHAR_U = 85;
const int CHAR_Z = 90; // zooming

bool key(int key) {
    return texelFetch(iChannel0, ivec2(key,2),0).x != 0.0;
}

vec3 diagram(vec2 z, float scale) {
  vec2 z0 = z;
  z.x += 0.5;
  z = Minv*z; // Convert to square grid
  ivec2 index = ivec2(floor(z)); // Remember cell in grid
  z -= floor(z);
  z = M*z; // Back to triangles

  // Map to equilateral triangles [(0,0),(1,0),(0.5,0.866)],[(0,0),(1,0),(0.5,-0.866)]
  z.x -= 0.5;
  bool inlower = z.y < 0.0; // Point is in lower triangle,
  if (inlower) z = -z;      // so rotate to upper triangle

  // All patterns have rotational symmetry about triangle centre, so:
  // map centre of triangle to origin...
  z.y -= 0.5*Y;

  // ...and reflect in planes of symmetry of triangle
  const vec2 A = 0.5*(vec2(1,X));  // normalized
  const vec2 B = 0.5*(vec2(-1,X)); // normalized
  float ta = dot(z,A);
  z -= 2.0*max(ta,0.0)*A;
  float tb = dot(z,B);
  z -= 2.0*max(tb,0.0)*B;
  z.y += 0.5*Y;        // Shift origin back to region centre,
  if ((ta > 0.0) != (tb > 0.0)) z.x = -z.x; // Want even number of reflections

  vec3 col = vec3(1);
  vec3 col3 = vec3(1,0,0);
  vec3 col4 = vec3(1,1,0);

  bool inregion = length(z-z0) < 1e-4;

  scale *= 2.0*X;
  z *= 2.0*X; // Scale so region is 1 by X

  float pwidth = scale*3.0/iResolution.y;
  float eps = 0.01;
  float lwidth0 = 2.0*eps;
  float lwidth1 = max(lwidth0+pwidth,4.0*eps);
  float bwidth = max(pwidth,1.0*eps);

  if (!key(CHAR_R)) {
    float d = min(abs(z.x),z.y);
    d = min(d,line(abs(z),vec2(0,1),vec2(X,0)));
    float mfact = smoothstep(-bwidth,bwidth,d);
    col = mix(col3,col4,(z.x < 0.0 ? mfact : mfact-1.0));
    if (key(CHAR_F) && !inregion) col = mix(col,0.8*col,mfact);
  }

  vec2 p = vec2(0.48,0.44); // The snub Wythoff point
  if (!key(CHAR_M) && iMouse.x > 0.0) {
    p = iMouse.xy/iResolution.y;
  }

  // Now reflect p in various lines of symmetry to get the
  // actually points of the tilings.
  // This really needs a picture.
  vec2 p0 = vec2(p.x,-p.y);
  vec2 p1 = vec2(-p.x,p.y);
  vec2 p2 = reflection(p,vec2(X,0),vec2(0,1));
  vec2 p3 = -p2;
  vec2 p4 = reflection(-p,vec2(-X,0),vec2(0,1));
  vec2 p5 = reflection(p,vec2(-X,0),vec2(0,1));
  vec2 p6 = reflection(p,vec2(-X,0),vec2(0,1));
  vec2 p7 = reflection(p1,vec2(-X,0),vec2(0,1));

  // The centre point of various triangles.
  // This needs a picture as well.
  vec2 t1 = (p0+p1+p2)/3.0;
  vec2 t2 = (p0+p1+p3)/3.0;
  vec2 t3 = (p1+p2+p6)/3.0;
  vec2 t4 = -(-t1-vec2(-X,0))+vec2(-X,0);
  vec2 t5 = (p1+p4+p6)/3.0;
  vec2 t6 = -(t5-vec2(-X,0))+vec2(-X,0);

  // Draw the lines. We have to draw all the lines that
  // intersect with the fundamental region - there are
  // quite a few, some of which only have a few pixels
  // in common.
  vec3 col1 = vec3(0.2);
  vec3 col2 = vec3(0.8);

  // Dual edges
  if (!key(CHAR_D)) {
    float d = 1e8;
    d = min(d,segment(z,t1,t2));
    d = min(d,segment(z,t1,vec2(X,0)));
    d = min(d,segment(z,t1,t3));
    d = min(d,segment(z,t1,t3));
    d = min(d,segment(z,-t1,t4));
    d = min(d,segment(z,-t1,t4));
    //d = min(d,segment(z,t5,t6));
    d = min(d,segment(z,p7,t3));
    //d = min(d,segment(z,p7,-p7));
    col = mix(col2,col,smoothstep(lwidth0,lwidth1,d));
  }

  // Main tiling edges
  if (!key(CHAR_T)) {
    float d = 1e8;
    d = min(d,segment(z,p0,p1));
    d = min(d,segment(z,p1,p2));
    d = min(d,segment(z,p2,p0));
    d = min(d,segment(z,p1,p3));
    d = min(d,segment(z,p1,p4));
    d = min(d,segment(z,p1,p5));
    col = mix(col1,col,smoothstep(lwidth0,lwidth1,d));
  }
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 z = (2.0*fragCoord-iResolution.xy+0.123)/iResolution.y;
  float scale = 1.0;
  if (key(CHAR_Z)) scale = exp(2.0*sin(iTime));
  z *= scale;
  if (key(CHAR_M) && iMouse.x > 0.0) {
    z -= (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  }
  vec3 col = diagram(z,scale);
  fragColor = vec4(col,1);
}

// variant of  https://shadertoy.com/view/3tKSzy

void mainImage( out vec4 O, vec2 U )
{
    O = sqrt( texture(iChannel0,U/iResolution.xy) );

    O = fract( 1e2* ( O - (O.r+O.g+O.b)/3. ));

    O *= O;
}

void mainImage( out vec4 O, vec2 U )
{
    U/=iResolution.xy;
    O = texture(iChannel0,U);
    if (U.x<.5) O = pow(O,vec4(1./2.2));                 // to flat space

    float l = U.y>.5 ? (O.r+O.g+O.b)/3.                  // isoluminance
                     : dot(O.rgb,vec3(.299,.587,.114));  // luma

    O = .5 + 20.*(O-l);                                  // amplifies difference with B&W

    if (U.x<.5) O = pow(O,vec4(2.2));                    // to sRGB
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0, uv);



    uv -= .5;

     vec3 mixing = col.gbr+col.brg;

    col.rgb+= mixing * mixing * 0.1;

    float aspect = iResolution.x/iResolution.y;

    uv.x*= aspect;

     vec4 bg = texture(iChannel1, uv * 4.6);

    uv *= 12.;



  	uv = floor(uv) * 0.5; // For Grid

    vec3 bgCol = (0.6 + bg.rgb *0.2 + mod(uv.x - uv.y, 1.) * 0.1);

    col.rgb =  (col.rgb  + (length(bg.rgb) - 0.4) * (1.1- col.a) * .5  ) * col.a + 
                        bgCol  * (1.-col.a);



    fragColor = vec4(col.rgb,1.0);
}

