bool isStoreArea( in vec2 savePos, in vec2 fragCoord)
{
    vec2 r = savePos - fragCoord;
    return length(r) < (1./200.);
}


void storeValue( in vec2 savePos, in vec4 va, inout vec4 fragColor, in vec2 fragCoord)
{
    vec2 r = savePos - fragCoord;
    fragColor = ( length(r) < (1./200.)) ? va : fragColor;
}

vec4 loadValue( in vec2 savePos )
{
    return texture( iChannel0, savePos);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 MousePos = iMouse.xy/iResolution.xy;


    const float SAMPLER_AREA = 0.9;
    const float BRIGHNESS_AREA = 0.85;
    const float PERSIST_AREA = 0.99;

    // PERSISTENCE
    vec2 persistanceUV = uv;

    const vec2 PRST_SIZE = vec2(800., 450.);

    const vec2 P_MOUSEPOS = (vec2(0., 449.) + 0.5) / PRST_SIZE;
    const vec2 BRUSH_COLOR = (vec2(10., 449.) + 0.5) / PRST_SIZE;
    const vec2 BRUSH_BRIGHTNESS = (vec2(20., 449.) + 0.5) / PRST_SIZE;
    const vec2 P_MOUSEVEC = (vec2(30., 449.) + 0.5) / PRST_SIZE;

    vec4 prevM = loadValue(P_MOUSEPOS);

    prevM.xy -= 0.5;
    float force = prevM.z; 
    float thickness = prevM.w;


    vec4 prevM_Vec = loadValue(P_MOUSEVEC);
    prevM_Vec.xy -= 0.5;
    float prevThickness = prevM_Vec.w;
    float prevLineLength = prevM_Vec.z;

    vec4 brushHUE = loadValue(BRUSH_COLOR);
    vec4 brushColor = brushHUE;
    brushColor.a = 1.;

    vec4 colorUE = loadValue(BRUSH_BRIGHTNESS);

    float brushBrightness = colorUE.x;
    float brushContrast = colorUE.y;

 	// COLOR MGMT
    uv -= .5;

    float aspect = iResolution.x/iResolution.y;

    vec2 realM = MousePos - .5;

    uv.x *= aspect;

    realM.x *= aspect;

    vec2 M = realM;// + prevM_Vec.xy * force;

    float lineLength = length(M-prevM.xy);

    if (persistanceUV.y<SAMPLER_AREA)
    {

        brushColor = mix(vec4(1.), brushHUE, brushContrast) *  brushBrightness;
        brushColor.a = 1.;

        if (persistanceUV.x < BRIGHNESS_AREA)
        {

            // PAINTING

            vec4 col = texture(iChannel0, persistanceUV);

            float distToNext = length(uv - M);

            float t = mix(thickness,prevThickness , clamp(distToNext/lineLength ,0.,1.)) + 0.0001;

            //M += (M-prevM.xy)*0.25;

            float minD = DistToLine(uv, M,prevM.xy);//  min(d0, d1);

            const float DIST_COEF = 0.005;

            float maxDist = DIST_COEF * 4. * t - minD;


            if (maxDist>0.) {


                const float SHARPNESS = 920.;

                vec2 direction = M - prevM.xy; 

                float lineLength = length(direction);

                float dist = 
                    lineLength > t *0.01 
                    ? DistToCurve(prevM.xy, prevM.xy+prevM_Vec.xy * lineLength * 0.3, M  , uv)
                    : DistToLine(uv, M,prevM.xy)
                        ; 


                float smudgeStep = floor(iTime * 2. );

                vec2 ruv = Rotate(uv,  smudgeStep * 123.456);
                float mask = texture(iChannel1, ruv + smudgeStep*0.123).b;

                //mask*= mask;

                float soft = (.001/dist);

                float toBlur =  max(0.,DIST_COEF * (1. + mask * 5.) * (t) -dist - lineLength
                                   ) * force * 15.;

                float toPaint = 

                    // NEON BRUSH:
                               // (.003/dist));

                    // NORMAL BRUSH:
                                max(0., (DIST_COEF * t * (1. + toBlur * .3) -dist)* SHARPNESS / t  * force //* min(1., 0.5 + lineLength*10.)
                                   ) ;

                float alpha = clamp(toPaint, 0., 1.);
                float rgbAlpha = clamp((alpha*2.+soft * maxDist * max(0., 1.-col.a))/(col.a + alpha), 0., 1.);
                vec4 maskb = vec4(rgbAlpha,rgbAlpha,rgbAlpha, alpha);

                // Pixel Offset For Blur
                vec2 off= (1./iResolution.xy)*0.5; // To sample area between pixels(Bilinear filtering would work as 4 blurring 4 pixels)

                vec2 blurUv = persistanceUV - prevM_Vec.xy * prevLineLength * lineLength * 400. * toBlur * maxDist;

                vec4 up = texture(iChannel0, blurUv + off);
                vec4 down = texture(iChannel0, blurUv + vec2(off.x,-off.y));
                vec4 left = texture(iChannel0, blurUv + vec2(-off.x, off.y)); 
                vec4 right = texture(iChannel0, blurUv - off);
                float total = up.a + down.a + left.a + right.a + 0.01;
                vec4 blurred = (up*up.a+down*down.a+left*left.a+right*right.a) / total;

                col = mix(col, blurred, min(1.,toBlur));



                fragColor = max(vec4(0.,0.,0.,0.), mix(col, brushColor, maskb));
                //fragColor.rgb = sqrt(fragColor.rgb);

               // fragColor.rgb = vec3(ruv*20.,0.);
            } else {
                fragColor = col;
            }

         } else 
         {
            float brightness = persistanceUV.y * SAMPLER_AREA;
            float contrast = (persistanceUV.x - BRIGHNESS_AREA) / (1. - BRIGHNESS_AREA);
            fragColor = mix(vec4(1.), brushHUE, contrast) *  brightness;
            fragColor.a = 1.;

            float dist = length(iResolution.yx * vec2(SAMPLER_AREA, 1. - BRIGHNESS_AREA ) * 0.02 * vec2(brushBrightness - brightness, brushContrast -contrast ));

            fragColor.rgb = mix(fragColor.rgb, vec3(1.), smoothstep(0.03, 0., abs(dist - 0.1)) );

        }

    } else {

    	// TOP PART

        float x = persistanceUV.x;

        float down = clamp(iMouse.z * 800., 0. , 1.);

        float click = down * max(0.,1. - force*100.); // Not down previous frame

    	force = clamp(down*iTimeDelta * 5. + force, 0. , 1.) * down;

        if (persistanceUV.y>PERSIST_AREA)
        {
            // STORING DATA

            realM = M;
            float path = length(prevM.xy - realM.xy);
            storeValue(P_MOUSEVEC, vec4(
                (lineLength>0.001 
                ? normalize(realM-prevM.xy)
                : vec2(0.,0.)) + 0.5,
                lineLength, thickness), fragColor, persistanceUV);

            thickness = mix(thickness, 1./(path*5. + .1) + 0.5, min(0.3,path*5.)) * down;

            storeValue(P_MOUSEPOS, vec4(realM + 0.5, force, thickness), fragColor, persistanceUV);

            if (isStoreArea(BRUSH_COLOR, persistanceUV))
            {

                float inSamplingArea = clamp((MousePos.y - SAMPLER_AREA) * 100., 0., 1.);

                fragColor = mix(brushColor, GetColorPalette(MousePos.x), click * inSamplingArea );

            } else if (isStoreArea(BRUSH_BRIGHTNESS, persistanceUV))
            {
                float hueSampler = clamp((MousePos.y - SAMPLER_AREA) * 100., 0., 1.);

                float inBrightnessArea = (1. - hueSampler) * clamp((MousePos.x - BRIGHNESS_AREA) * 1000., 0., 1.);

                brushBrightness = mix(brushBrightness, MousePos.y * SAMPLER_AREA, click * inBrightnessArea);
                brushContrast = mix(brushContrast, (MousePos.x - BRIGHNESS_AREA) / (1. - BRIGHNESS_AREA) , click * inBrightnessArea);

                fragColor = vec4(brushBrightness, brushContrast, 0, 0);
            }

        } else {
            // PALETTE
            fragColor = GetColorPalette(x);

            float dist = length(brushColor.rgb - fragColor.rgb);

            fragColor.rgb = mix(fragColor.rgb, vec3(1.), smoothstep(0.01, 0., dist));

        }
    }

}

